    bucket1: 0,
    bucket2: 0,
    bucket3: 0
  };
  getStrategyList.data.forEach(item => {
    if (item.bucketId === 1) {
      counters.bucket1++;
    } else if (item.bucketId === 2) {
      counters.bucket2++;
    } else if (item.bucketId === 3) {
      counters.bucket3++;
    }
    return counters;
  });
  return counters;
}

async function () {
  await getStrategyList.run();
  await getUserTasks.run();
  getProcessReadBucket.run();
  getProcessDischarged.run();
  getProcessChangeSegment.run();
  getProcessAddItem.run();
  BonitaScripts.countBuckets();
}

