actionConfiguration.body:
async function (caseId) {
  const paramsCaseId = {
    "caseId": caseId
  };
  let caseData = {};
  let processDefinitionId = 0;
  let casePromise;
  let caseArchivedPromise = {};
  await getCaseInfo.run(paramsCaseId);
  casePromise = getCase.run(paramsCaseId).then(() => {
    processDefinitionId = getCase.data.processDefinitionId.id;
    caseData = getCase.data;
  }).catch(() => {
    caseArchivedPromise = getArchivedCase.run(paramsCaseId);
  });
  await Promise.all([caseArchivedPromise, casePromise]);
  if (getCase.responseMeta.isExecutionSuccess) {
    processDefinitionId = getCase.data.processDefinitionId.id;
    caseData = getCase.data;
  } else {
    console.log("Archived ID: ", getArchivedCase.data);
    processDefinitionId = getArchivedCase.data[0].processDefinitionId.id;
    caseData = getArchivedCase.data[0];
  }
  const paramsProcessId = {
    "processDefinitionId": processDefinitionId
  };
  getDiagram.run(paramsProcessId);
  BPMNVisuModel.model = {
    bpmnDiagram: getDiagram.data,
    processName: caseData.processDefinitionId.name,
    flowNodeStatesCounters: getCaseInfo.data.flowNodeStatesCounters
  };
  return BPMNVisuModel.model;
}

