export default {

	selectedRequest: [],
	defaultAvatarBase64: "data:image/jpeg;base64,",

	async bonitaOnPageLoad () {
		//Modify API GET Queries to adapt for your case
		await getStrategyList.run();
		await getUserTasks.run();
		getProcessReadBucket.run();
		getProcessDischarged.run();
		getProcessChangeSegment.run();
		getProcessAddItem.run();
		this.countBuckets();
	},	

	async refreshData() {
		getStrategyList.run();
		getUserTasks.run();
		this.bonitaOnPageLoad();
	},

	getLocalDateToday () {
		const localDate = new Date().toISOString();
		return localDate
	},

	forgeDocumentImageURL (documentContext){
		const forgedURL = appsmith.URL.protocol + "//" + appsmith.URL.hostname + "/bonita/API/formsDocumentImage?fileName="+ encodeURIComponent(documentContext.fileName) + "&document=" + encodeURIComponent(documentContext.id);
		return forgedURL;
	},

	forgeDownloadDocumentURL (documentURL){
		const forgedURL = appsmith.URL.protocol + "//" + appsmith.URL.hostname + "/bonita/API/" + documentURL;
		return forgedURL;
	},

	forgeAvatarURL (iconId){
		let avatar = ""
		if (iconId == "icons/default/icon_user.png" || iconId == null){
			avatar = this.defaultAvatarBase64;
		}else{
			avatar = appsmith.URL.protocol + "//" + appsmith.URL.hostname + "/bonita/API/avatars/" + iconId;
		}
		return avatar
	},

	showTaskModal(caseId){
		return getUserTasks.data.some(item => item.parentCaseId == caseId);
