		this.selectedRequest = request;
		let taskData = this.renameAttribute(getUserTasks.data, "name", "taskName");
		taskData = this.renameAttribute(taskData,"id","taskId");
		const task = taskData.find(item => item.parentCaseId == request.estrategiaId);
		this.selectedRequest.taskData = task;
		return this.selectedRequest;
	},

	setSelectedRequest(request){
		this.selectedRequest = request;
		return this.selectedRequest;
	},//CaseIDKey is the attribute used in the bdm object to store the processInstanceId

	combineTasksBDM(taskData,bdmData,caseIdKey) {
		return taskData.map(row=> {
			var taskOrder = bdmData.find(order => order[caseIdKey] == row.estrategiaId)
			return taskOrder ? { ...row, ...taskOrder } : null;
		}).filter(row => row !== null); // Filter out rows where taskOrder was not found
	},

	renameAttribute(array, oldKey, newKey) {
		return array.map(item => {
			if (item.hasOwnProperty(oldKey)) {
				item[newKey] = item[oldKey];
				delete item[oldKey];
			}
			return item;
		});
	},
	countBuckets() {
		// Initialize counters for each status
		let counters = {
			bucket1: 0,
			bucket2: 0,
			bucket3: 0
		};
		getStrategyList.data.forEach(item => {
			// Check the "statut" and increment the corresponding counter
			if (item.bucketId === 1) {
				counters.bucket1++;
			} else if (item.bucketId === 2) {
				counters.bucket2++;
			} else if (item.bucketId === 3) {
				counters.bucket3++
			}	
			return counters;
		});

		// Return the count object
		return counters;
	},
	getStrategyName(segmentId){
		const strategies = [
			{ "segmentId": 11, "strategyName": "No contactado" },
			{ "segmentId": 21, "strategyName": "Acuerdo de pago" },
			{ "segmentId": 31, "strategyName": "Sin acuerdo/negativa" },
			{ "segmentId": 41, "strategyName": "Incumplimiento de pago" },
			{ "segmentId": 51, "strategyName": "Ad-hoc" }
		];

		// Find the strategy object with the matching segmentId
		const strategy = strategies.find(item => item.segmentId === segmentId);

		// If a match is found, return the strategyName, otherwise return a default message
		return strategy ? strategy.strategyName : 'Estrategia no encontrada';
	},
	actions :[
		{"id": "01", "name": "Acción 1", "tipo": "", "cuerpo": ""},
		{"id": "02", "name": "Acción 2", "tipo": "", "cuerpo": ""},
		{"id": "03", "name": "Acción 3", "tipo": "", "cuerpo": ""}
	],
	tiposAcciones:[
		{
			"name": "Llamada",
			"code": "LLAMADA"
		},
		{
			"name": "SMS",
			"code": "SMS"
		},
		{
			"name": "SMS certificado",
			"code": "SMS_CERTIFICADO"
		},
		{
			"name": "Mail",
			"code": "MAIL"
		},
		{
			"name": "Carta",
			"code": "CARTA"
		},
		{
			"name": "Skiptracing",
			"code": "SKIPTRACING"
		},
		{
			"name": "Burofax",
			"code": "BUROFAX"
		}
	],
	formatDate(dateISO) {
		// Create a new Date object from the input string
		const date = new Date(dateISO);

		// Get the components
		const day = String(date.getDate()).padStart(2, '0');  // Pad single digits with leading zero
		const month = String(date.getMonth() + 1).padStart(2, '0');  // getMonth() is zero-indexed
		const year = date.getFullYear();
		const hours = String(date.getHours()).padStart(2, '0');
		const minutes = String(date.getMinutes()).padStart(2, '0');

		// Format the date in "DD/MM/YYYY HH:mm" format
		return `${day}/${month}/${year} ${hours}:${minutes}`;
	}

}

