srcDoc.css:
html, body {
  margin: 0;
  padding: 0;
  height: auto;
  overflow: visible;
}

.ant-card .ant-card-body {
  padding: 0;
}

.app {
  width: calc(1px * var(--appsmith-ui-width));
  justify-content: center;
  border-radius: 0px;
  border: none;
  box-shadow: none;
  background: transparent;
  color: #1e3a8a;
  overflow: visible;
}
.app .process-list-container {
  padding: 20px;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
}
.app .process-list-container .process-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
  padding: 0px;
  background: transparent;
  border-radius: 0px;
  border: none;
}
.app .process-list-container .process-header .header-left {
  display: flex;
  align-items: center;
  gap: 12px;
}
.app .process-list-container .process-header .header-left h3 {
  margin: 0;
  color: #1e3a8a;
  font-size: 18px;
  font-weight: 600;
}
.app .process-list-container .process-header .header-left .process-count {
  background: #1e3a8a;
  color: white;
  padding: 4px 8px;
  border-radius: 0px;
  font-size: 12px;
  font-weight: 500;
}
.app .process-list-container .process-header .header-right {
  display: flex;
  gap: 12px;
  align-items: center;
}
.app .process-list-container .process-header .header-right .search-input {
  padding: 8px 12px;
  border: 1px solid #d1d5db;
  border-radius: 0px;
  font-size: 14px;
  width: 200px;
  transition: all 0.3s ease;
}
.app .process-list-container .process-header .header-right .search-input:focus {
  outline: none;
  border-color: #1e3a8a;
  box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
}
.app .process-list-container .process-header .header-right .status-filter {
  padding: 8px 12px;
  border: 1px solid #d1d5db;
  border-radius: 0px;
  font-size: 14px;
  background: white;
  cursor: pointer;
  transition: all 0.3s ease;
}
.app .process-list-container .process-header .header-right .status-filter:focus {
  outline: none;
  border-color: #1e3a8a;
  box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
}
.app .process-list-container .process-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  gap: 20px;
  margin-bottom: 24px;
  align-items: stretch;
}
.app .process-list-container .process-grid .process-card {
  background: white;
  border: 2px solid #e2e8f0;
  border-radius: 0px;
  padding: 20px;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}
.app .process-list-container .process-grid .process-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 24px rgba(30, 58, 138, 0.15);
  border-color: #1e3a8a;
}
.app .process-list-container .process-grid .process-card.selected {
  border-color: #1e3a8a;
  background: #f0f4ff;
  box-shadow: 0 8px 16px rgba(30, 58, 138, 0.2);
}
.app .process-list-container .process-grid .process-card.error-status {
  border-color: #ef4444;
  box-shadow: 0 4px 12px rgba(239, 68, 68, 0.2);
}
.app .process-list-container .process-grid .process-card.error-status:hover {
  border-color: #dc2626;
  box-shadow: 0 8px 20px rgba(239, 68, 68, 0.3);
}
.app .process-list-container .process-grid .process-card .process-header-card {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 16px;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-title {
  flex: 1;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-title h4 {
  margin: 0 0 8px 0;
  color: #1e3a8a;
  font-size: 16px;
  font-weight: 600;
  line-height: 1.3;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-title .status-badge .ant-badge-status-dot {
  width: 8px;
  height: 8px;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-title .status-badge .ant-badge-status-text {
  font-size: 12px;
  font-weight: 500;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions {
  display: flex;
  gap: 4px;
  opacity: 0;
  transition: opacity 0.3s ease;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn {
  width: 28px;
  height: 28px;
  border: none;
  border-radius: 0px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  background: rgba(30, 58, 138, 0.1);
  color: #1e3a8a;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn:hover {
  background: rgba(30, 58, 138, 0.2);
  transform: scale(1.1);
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.edit-btn:hover {
  background: rgba(59, 130, 246, 0.2);
  color: #3b82f6;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.duplicate-btn:hover {
  background: rgba(16, 185, 129, 0.2);
  color: #10b981;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.archive-btn:hover {
  background: rgba(245, 158, 11, 0.2);
  color: #f59e0b;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.view-cases-btn:hover {
  background: rgba(99, 102, 241, 0.2);
  color: #6366f1;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.delete-btn:hover {
  background: rgba(239, 68, 68, 0.2);
  color: #ef4444;
}
.app .process-list-container .process-grid .process-card:hover .process-actions {
  opacity: 1;
}
.app .process-list-container .process-grid .process-card .process-content .process-description {
  color: #64748b;
  font-size: 14px;
  line-height: 1.5;
  margin: 0 0 16px 0;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}
.app .process-list-container .process-grid .process-card .process-content .process-meta {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
  margin-bottom: 16px;
  padding: 12px;
  background: #f8fafc;
  border-radius: 0px;
}
.app .process-list-container .process-grid .process-card .process-content .process-meta .meta-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}
.app .process-list-container .process-grid .process-card .process-content .process-meta .meta-item .meta-label {
  font-size: 11px;
  color: #64748b;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 4px;
}
.app .process-list-container .process-grid .process-card .process-content .process-meta .meta-item .meta-value {
  font-size: 14px;
  color: #1e3a8a;
  font-weight: 600;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 8px;
  margin-bottom: 16px;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 8px;
  border-radius: 0px;
  background: #f8fafc;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-label {
  font-size: 11px;
  color: #64748b;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 4px;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value {
  font-size: 16px;
  font-weight: 700;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value.open {
  color: #f59e0b;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value.closed {
  color: #15803d;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value.errors {
  color: #ef4444;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value.cancelled {
  color: #000000;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value.total {
  color: #1e3a8a;
}
.app .process-list-container .process-grid .process-card .process-content .process-dates {
  display: flex;
  flex-direction: column;
  gap: 6px;
  padding: 12px;
  background: #f8fafc;
  border-radius: 0px;
}
.app .process-list-container .process-grid .process-card .process-content .process-dates .date-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.app .process-list-container .process-grid .process-card .process-content .process-dates .date-item .date-label {
  font-size: 12px;
  color: #64748b;
  font-weight: 500;
}
.app .process-list-container .process-grid .process-card .process-content .process-dates .date-item .date-value {
  font-size: 12px;
  color: #1e3a8a;
  font-weight: 600;
}
.app .process-list-container .add-process-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  /*padding: 20px;*/
  background: #f8fafc;
  border: 2px dashed #1e3a8a;
  box-sizing: border-box;
  border-radius: 0px;
  cursor: pointer;
  transition: all 0.3s ease;
  height: 100%;
  min-height: 200px;
}
.app .process-list-container .add-process-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 24px rgba(30, 58, 138, 0.15);
  border-color: #1e3a8a;
  background: rgba(30, 58, 138, 0.05);
}
.app .process-list-container .add-process-card .add-icon {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #1e3a8a;
  margin-bottom: 12px;
  transition: all 0.3s ease;
}
.app .process-list-container .add-process-card .add-text {
  font-weight: 600;
  font-size: 14px;
  color: #1e3a8a;
  line-height: 1.3;
  text-align: center;
}

/* Responsive */
@media (max-width: 768px) {
  .app .process-list-container {
    padding: 16px;
  }
  .app .process-list-container .process-header {
    flex-direction: column;
    gap: 16px;
    align-items: stretch;
  }
  .app .process-list-container .process-header .header-right {
    flex-direction: column;
    gap: 8px;
  }
  .app .process-list-container .process-header .header-right .search-input,
  .app .process-list-container .process-header .header-right .status-filter {
    width: 100%;
  }
  .app .process-list-container .process-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }
}
/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.process-card {
  animation: fadeIn 0.3s ease-out;
}

