html, body {
  margin: 0;
  padding: 0;
  height: auto;
  overflow: visible;
}

.ant-card .ant-card-body {
  padding: 0;
}

.app {
  width: calc(1px * var(--appsmith-ui-width));
  justify-content: center;
  border-radius: 0px;
  border: none;
  box-shadow: none;
  background: transparent;
  color: #1e3a8a;
  overflow: visible;
}
.app .process-list-container {
  padding: 20px;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
}
.app .process-list-container .process-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
  padding: 0px;
  background: transparent;
  border-radius: 0px;
  border: none;
}
.app .process-list-container .process-header .header-left {
  display: flex;
  align-items: center;
  gap: 12px;
}
.app .process-list-container .process-header .header-left h3 {
  margin: 0;
  color: #1e3a8a;
  font-size: 18px;
  font-weight: 600;
}
.app .process-list-container .process-header .header-left .process-count {
  background: #1e3a8a;
  color: white;
  padding: 4px 8px;
  border-radius: 0px;
  font-size: 12px;
  font-weight: 500;
}
.app .process-list-container .process-header .header-right {
  display: flex;
  gap: 12px;
  align-items: center;
}
.app .process-list-container .process-header .header-right .search-input {
  padding: 8px 12px;
  border: 1px solid #d1d5db;
  border-radius: 0px;
