.app .process-list-container .process-header .header-right .search-input:focus {
  outline: none;
  border-color: #1e3a8a;
  box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
}
.app .process-list-container .process-header .header-right .status-filter {
  padding: 8px 12px;
  border: 1px solid #d1d5db;
  border-radius: 0px;
  font-size: 14px;
  background: white;
  cursor: pointer;
  transition: all 0.3s ease;
}
.app .process-list-container .process-header .header-right .status-filter:focus {
  outline: none;
  border-color: #1e3a8a;
  box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
}
.app .process-list-container .process-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
