  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  background: rgba(30, 58, 138, 0.1);
  color: #1e3a8a;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn:hover {
  background: rgba(30, 58, 138, 0.2);
  transform: scale(1.1);
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.edit-btn:hover {
  background: rgba(59, 130, 246, 0.2);
  color: #3b82f6;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.duplicate-btn:hover {
  background: rgba(16, 185, 129, 0.2);
  color: #10b981;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.archive-btn:hover {
  background: rgba(245, 158, 11, 0.2);
  color: #f59e0b;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.view-cases-btn:hover {
  background: rgba(99, 102, 241, 0.2);
  color: #6366f1;
}
.app .process-list-container .process-grid .process-card .process-header-card .process-actions .action-btn.delete-btn:hover {
  background: rgba(239, 68, 68, 0.2);
  color: #ef4444;
}
.app .process-list-container .process-grid .process-card:hover .process-actions {
  opacity: 1;
}
.app .process-list-container .process-grid .process-card .process-content .process-description {
  color: #64748b;
