}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-label {
  font-size: 11px;
  color: #64748b;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 4px;
}
.app .process-list-container .process-grid .process-card .process-content .process-stats .stat-item .stat-value {
