  height: 48px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #1e3a8a;
  margin-bottom: 12px;
  transition: all 0.3s ease;
}
.app .process-list-container .add-process-card .add-text {
  font-weight: 600;
  font-size: 14px;
  color: #1e3a8a;
  line-height: 1.3;
  text-align: center;
}

/* Responsive */
@media (max-width: 768px) {
  .app .process-list-container {
    padding: 16px;
  }
  .app .process-list-container .process-header {
    flex-direction: column;
    gap: 16px;
    align-items: stretch;
  }
  .app .process-list-container .process-header .header-right {
    flex-direction: column;
    gap: 8px;
  }
  .app .process-list-container .process-header .header-right .search-input,
  .app .process-list-container .process-header .header-right .status-filter {
    width: 100%;
  }
  .app .process-list-container .process-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }
}
/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.process-card {
  animation: fadeIn 0.3s ease-out;
}

