import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card, Badge, Tooltip } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'
import Markdown from 'https://cdn.jsdelivr.net/npm/react-markdown@9.0.1/+esm';

function App() {
	const [selectedProcess, setSelectedProcess] = React.useState(null);
	const [searchTerm, setSearchTerm] = React.useState('');
	const [statusFilter, setStatusFilter] = React.useState('all');

	const processes = appsmith.model.processes || [];

	// Filtrer les processus
	const filteredProcesses = processes.filter(process => {
		const matchesSearch = process.displayName.toLowerCase().includes(searchTerm.toLowerCase()) ||
							 process.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
							 process.user.toLowerCase().includes(searchTerm.toLowerCase());
		const matchesStatus = statusFilter === 'all' || process.statut === statusFilter;
		return matchesSearch && matchesStatus;
	});

	const handleProcessClick = (process) => {
		setSelectedProcess(process);
		appsmith.triggerEvent("onProcessClick", {
			process: process
		});
	};

	const handleAddProcess = () => {
		appsmith.triggerEvent("onAddProcessClick");
	};

	const handleEditProcess = (process, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onEditProcessClick", {
			process: process
		});
	};

	const handleDeleteProcess = (process, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onDeleteProcessClick", {
			process: process
		});
	};

	const handleDuplicateProcess = (process, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onDuplicateProcessClick", {
			process: process
		});
	};

	const handleArchiveProcess = (process, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onArchiveProcessClick", {
			process: process
		});
	};

	const handleViewCases = (process, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onViewCasesClick", {
			process: process
		});
	};

	// Check if process has never been used (no open or closed cases)
	const isProcessUnused = (process) => {
		return (process.openCases === null || process.openCases === 0) && 
			   (process.closedCases === null || process.closedCases === 0);
	};

	const getStatusColor = (status) => {
		switch (status) {
			case 'Running': return 'success';
			case 'Stopped': return 'error';
			case 'Creation': return 'processing';
			case 'Scheduled': return 'warning';
			case 'Archived': return 'default';
			case 'Error': return 'error';
			default: return 'default';
		}
	};

	const getStatusText = (status) => {
		switch (status) {
			case 'Running': return 'Running';
			case 'Stopped': return 'Stopped';
			case 'Creation': return 'Creation';
			case 'Scheduled': return 'Scheduled';
			case 'Archived': return 'Archived';
			case 'Error': return 'Error';
			default: return status;
		}
	};

	const formatDate = (dateString) => {
		if (!dateString) return '-';
		return new Date(dateString).toLocaleDateString('en-US');
	};

	return (
		<Card className="app">
			<div className="process-list-container">
				{/* Header with filters */}
				<div className="process-header">
					<div className="header-left">
						<h3>Process List</h3>
						<span className="process-count">{filteredProcesses.length} processes</span>
					</div>
					<div className="header-right">
						<input
							type="text"
							placeholder="Search for a process..."
							value={searchTerm}
							onChange={(e) => setSearchTerm(e.target.value)}
							className="search-input"
						/>
						<select
							value={statusFilter}
							onChange={(e) => setStatusFilter(e.target.value)}
							className="status-filter"
						>
							<option value="all">All statuses</option>
							<option value="Running">Running</option>
							<option value="Stopped">Stopped</option>
							<option value="Creation">Creation</option>
							<option value="Scheduled">Scheduled</option>
							<option value="Archived">Archived</option>
							<option value="Error">Error</option>
						</select>
					</div>
				</div>

				{/* Process list */}
				<div className="process-grid">
					{/* Add Process Button - First tile */}
					<div className="add-process-card" onClick={handleAddProcess}>
						<div className="add-icon">
							<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
								<path d="M12 4V20M4 12H20" stroke="white" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
							</svg>
						</div>
						<div className="add-text">Add Process</div>
					</div>

					{filteredProcesses.map((process) => (
						<div
							key={process.id}
							className={`process-card ${selectedProcess?.id === process.id ? 'selected' : ''} ${process.statut === 'Error' ? 'error-status' : ''}`}
							onClick={() => handleProcessClick(process)}
						>
							<div className="process-header-card">
								<div className="process-title">
									<h4>{process.displayName}</h4>
									<Badge 
										status={getStatusColor(process.statut)} 
										text={getStatusText(process.statut)}
										className="status-badge"
									/>
								</div>
								<div className="process-actions">
									<Tooltip title="Edit">
										<button
											className="action-btn edit-btn"
											onClick={(e) => handleEditProcess(process, e)}
										>
											<svg width="14" height="14" viewBox="0 0 24 24" fill="none">
												<path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
