.app {
  width: 100%;
  padding: 0;
  background: #ffffff;
  overflow: visible;
  margin: 0;
  line-height: 1.2;
  height: auto;
  min-height: 0;
}

html, body {
  margin: 0;
  padding: 0;
  height: auto;
  overflow: visible;
}

.admin-container {
  width: 100%;
  margin: 0;
  padding: 0;
}

.sections-overview {
  margin: 0;
  padding: 0;
}

.sections-overview .ant-card {
  border-radius: 0px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  margin-bottom: 4px;
  border: 1px solid #e8e8e8;
}

.sections-overview .ant-card:last-child {
  margin-bottom: 0;
}

.sections-overview .ant-card:hover {
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.15);
}

.sections-overview .ant-card-head {
  background: #f5f5f5;
  color: #333;
  border-radius: 0;
  padding: 2px 4px;
  border-bottom: 1px solid #e8e8e8;
  margin: 0;
  line-height: 1.2;
}

.sections-overview .ant-card-head-title {
  color: #333;
  padding: 0;
  font-weight: 600;
  font-size: 12px;
  margin: 0;
}

.sections-overview .ant-card-extra {
  color: #666;
  padding: 0;
  font-size: 10px;
  margin: 0;
}

.sections-overview .ant-card-body {
  padding: 2px 4px;
  margin: 0;
  line-height: 1.2;
}

.export-section {
  margin-top: 10px;
}

.export-section .ant-card-body {
  padding: 8px;
}

.ant-list-item {
  padding: 1px 0;
  margin-bottom: 1px;
  background: transparent;
  border: none;
  transition: all 0.3s ease;
  border-bottom: 1px solid #d9d9d9;
  line-height: 1.2;
}

.ant-list-item:last-child {
  margin-bottom: 0;
  border-bottom: none;
}

.ant-list-item:hover {
  background: #fafafa;
  border-color: #d9d9d9;
}

.ant-tag {
  font-weight: 500;
  margin: 0 2px 0 0;
  font-size: 8px;
  padding: 0 3px;
  line-height: 1.2;
}

.ant-typography {
  margin: 0;
  padding: 0;
  line-height: 1.2;
}

.ant-list {
  margin: 0;
  padding: 0;
  background: transparent;
  line-height: 1.2;
}

.ant-list .ant-list-item {
  border: none;
  background: transparent;
  padding: 1px 0;
  margin-bottom: 1px;
  border-bottom: 1px solid #d9d9d9;
  line-height: 1.2;
}

.ant-list .ant-list-item:last-child {
  border-bottom: none;
  margin-bottom: 0;
}

.ant-list .ant-list-item:hover {
  background: #fafafa;
}

/* Responsive design */
@media (max-width: 768px) {
  .app {
    padding: 0;
    margin: 0;
  }
  .admin-container {
    margin: 0;
    padding: 0;
  }
  .sections-overview .ant-card {
    margin-bottom: 2px;
  }
  .export-section {
    margin-top: 8px;
  }
  .sections-overview .ant-card-head {
    padding: 1px 2px;
  }
  .sections-overview .ant-card-body {
    padding: 1px 2px;
  }
  .sections-overview .ant-card-head-title {
    font-size: 11px;
  }
  .sections-overview .ant-card-extra {
    font-size: 9px;
  }
}

