@charset "UTF-8";
html, body {
  margin: 0;
  padding: 0;
  height: auto;
  overflow: visible;
}

.ant-card .ant-card-body {
  padding: 0;
}

.app {
  width: calc(1px * var(--appsmith-ui-width));
  justify-content: center;
  border-radius: 0px;
  border: none;
  box-shadow: none;
  background: transparent;
  color: #1e3a8a;
  overflow: visible;
}
.app .bpmn-container {
  margin-bottom: 0px;
  width: 85%;
  margin: 0 auto;
  padding-top: 5px;
}
.app .bpmn-container .bpmn-flow {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  margin-bottom: 0px;
  /*padding: 20px;*/
  overflow-y: visible;
}
.app .bpmn-container .bpmn-flow .bpmn-step {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 16px 12px;
  background: #f8fafc;
  border-radius: 0px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 2px solid #1e3a8a;
  position: relative;
  width: 90%;
  height: 100px;
  text-align: center;
  box-shadow: 0 2px 2px rgba(30, 58, 138, 0.1);
}
.app .bpmn-container .bpmn-flow .bpmn-step:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 8px rgba(30, 58, 138, 0.15);
  border-color: #1e3a8a;
}
.app .bpmn-container .bpmn-flow .bpmn-step.active {
  background: #1e3a8a;
  border: 2px solid #1e3a8a;
  box-shadow: 0 8px 8px rgba(30, 58, 138, 0.3);
  transform: scale(1.02);
  color: white;
}
.app .bpmn-container .bpmn-flow .bpmn-step.active .step-icon {
  background: #ffffff;
  color: #1e3a8a;
  border: none;
}
.app .bpmn-container .bpmn-flow .bpmn-step.active .step-content .step-title, .app .bpmn-container .bpmn-flow .bpmn-step.active .step-content .step-ref {
  color: white;
}
.app .bpmn-container .bpmn-flow .bpmn-step.completed {
  background: #f8fafc;
  border-color: #1e3a8a;
}
.app .bpmn-container .bpmn-flow .bpmn-step.completed .step-icon {
  background: #1e3a8a;
  color: white;
  border: none;
}
.app .bpmn-container .bpmn-flow .bpmn-step.pending {
  background: #f8fafc;
  border-color: #1e3a8a;
}
.app .bpmn-container .bpmn-flow .bpmn-step.pending .step-icon {
  background: #1e3a8a;
  color: white;
  border: none;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-icon {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  font-weight: bold;
  background: #1e3a8a;
  margin-bottom: 12px;
  transition: all 0.3s ease;
  border: none;
  color: white;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-content .step-title {
  font-weight: 600;
  font-size: 12px;
  color: #1e3a8a;
  margin-bottom: 4px;
  line-height: 1.3;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-content .step-ref {
  font-size: 10px;
  color: #64748b;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-actions {
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  flex-direction: column;
  gap: 4px;
  opacity: 0;
  transition: opacity 0.3s ease;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-actions .move-btn {
  width: 24px;
  height: 24px;
  border: none;
  background: rgba(30, 58, 138, 0.1);
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  color: #1e3a8a;
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-actions .move-btn:hover {
  background: rgba(30, 58, 138, 0.2);
  transform: scale(1.1);
}
.app .bpmn-container .bpmn-flow .bpmn-step .step-actions .move-btn svg {
  filter: drop-shadow(0 1px 2px rgba(30, 58, 138, 0.1));
}
.app .bpmn-container .bpmn-flow .bpmn-step.active .step-actions .move-btn {
  color: #1e3a8a;
  background: rgba(255, 255, 255, 0.2);
}
.app .bpmn-container .bpmn-flow .bpmn-step.active .step-actions .move-btn:hover {
  background: rgba(255, 255, 255, 0.3);
}
.app .bpmn-container .bpmn-flow .bpmn-step:hover .step-actions {
  opacity: 1;
}
.app .bpmn-container .bpmn-flow .flow-arrow {
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  height: 40px;
}
.app .bpmn-container .bpmn-flow .flow-arrow.vertical-arrow svg {
  filter: drop-shadow(0 1px 2px rgba(30, 58, 138, 0.1));
}
.app .bpmn-container .bpmn-flow .add-step-button {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 16px 12px;
  background: #f8fafc;
  border-radius: 0px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 2px dashed #1e3a8a;
  position: relative;
  width: 90%;
  height: 100px;
  text-align: center;
  box-shadow: 0 2px 4px rgba(30, 58, 138, 0.1);
}
.app .bpmn-container .bpmn-flow .add-step-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(30, 58, 138, 0.15);
  border-color: #1e3a8a;
  background: rgba(30, 58, 138, 0.05);
}
.app .bpmn-container .bpmn-flow .add-step-button .add-icon {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #1e3a8a;
  margin-bottom: 12px;
  transition: all 0.3s ease;
  border: none;
}
.app .bpmn-container .bpmn-flow .add-step-button .add-text {
  font-weight: 600;
  font-size: 12px;
  color: #1e3a8a;
  line-height: 1.3;
}
.app .bpmn-container .bpmn-container::after {
  content: "";
  display: block;
  height: 20px; /* ou une valeur suffisante pour forcer Appsmith à étendre */
}

/* Animations */
@keyframes bounceIn {
  0% {
    transform: scale(0);
  }
  50% {
    transform: scale(1.2);
  }
  100% {
    transform: scale(1);
  }
}

