import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Card, List, Tag, Typography, Space, Button, Modal, Form, Input, Select, Switch, Row, Col } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'

const { Title, Text } = Typography;

function App() {
	const [sections, setSections] = React.useState([]);

	// Hover states for all interactive elements
	const [hoverStates, setHoverStates] = React.useState({});

	// Edit modal states
	const [isEditModalVisible, setIsEditModalVisible] = React.useState(false);
	const [editingItem, setEditingItem] = React.useState(null);
	const [editFormData, setEditFormData] = React.useState({});

	const setHoverState = (key, value) => {
		setHoverStates(prev => ({
			...prev,
			[key]: value
		}));
	};

	// Initialize sections from Appsmith model (only once)
	React.useEffect(() => {
		if (appsmith && appsmith.model && appsmith.model.sections) {
			setSections([...appsmith.model.sections]);
		}
	}, []); // Empty dependency array - only run once

	// Helper function to flatten groups hierarchy
	const flattenGroups = (groups, prefix = '', level = 0) => {
		let result = [];
		groups.forEach(group => {
			const fullPath = prefix ? `${prefix} > ${group.label}` : group.label;
			result.push({
				label: fullPath,
				value: group.value,
				level: level,
				hasChildren: group.children && group.children.length > 0
			});
			if (group.children && group.children.length > 0) {
				result = result.concat(flattenGroups(group.children, fullPath, level + 1));
			}
		});
		return result;
	};

	// Helper function to get all children values recursively
	const getAllChildrenValues = (groups, parentValue) => {
		let children = [];
		groups.forEach(group => {
			if (group.value === parentValue) {
				if (group.children && group.children.length > 0) {
					group.children.forEach(child => {
						children.push(child.value);
						children = children.concat(getAllChildrenValues(group.children, child.value));
					});
				}
			} else if (group.children && group.children.length > 0) {
				children = children.concat(getAllChildrenValues(group.children, parentValue));
			}
		});
		return children;
	};

	const handleFieldEdit = (sectionIndex, fieldIndex, field) => {
		setEditingItem({
			type: 'field',
			sectionIndex,
			fieldIndex,
			data: { ...field }
		});
		setEditFormData({
			label: field.options.label,
			placeholder: field.options.placeholder,
			type: field.type,
			ref: field.options.ref,
			// Field-specific properties
			...(field.type === 'Select' && { options: field.options.options?.join('\n') }),
			...(field.type === 'Number' && { 
				maxValue: field.options.maxValue,
				minValue: field.options.minValue,
				numberOfDigits: field.options.numberOfDigits,
				unit: field.options.unit
			}),
			...(field.type === 'Textarea' && { maxLength: field.options.maxLength }),
			...(field.type === 'CheckBox' && { 
				options: field.options.options?.join('\n'),
				allowMultiple: field.options.allowMultiple
			}),
			...(field.type === 'User selector' && { 
				roles: field.options.roles || [],
				groups: field.options.groups || []
			})
		});
		setIsEditModalVisible(true);
	};

	const handleSectionEdit = (sectionIndex, section) => {
		setEditingItem({
			type: 'section',
			sectionIndex,
			data: { ...section }
		});
		setEditFormData({
			title: section.title,
			description: section.description,
			ref: section.ref
		});
		setIsEditModalVisible(true);
	};

	const handleMoveSectionUp = (sectionIndex) => {
		if (sectionIndex > 0) {
			const newSections = [...sections];
			const temp = newSections[sectionIndex];
			newSections[sectionIndex] = newSections[sectionIndex - 1];
			newSections[sectionIndex - 1] = temp;
			setSections(newSections);
			triggerSave();
		}
	};

	const handleMoveSectionDown = (sectionIndex) => {
		if (sectionIndex < sections.length - 1) {
			const newSections = [...sections];
			const temp = newSections[sectionIndex];
			newSections[sectionIndex] = newSections[sectionIndex + 1];
			newSections[sectionIndex + 1] = temp;
			setSections(newSections);
			triggerSave();
		}
	};

	const handleMoveFieldUp = (sectionIndex, fieldIndex) => {
		if (fieldIndex > 0) {
			const newSections = [...sections];
			const section = newSections[sectionIndex];
			const newData = [...section.data];
			const temp = newData[fieldIndex];
			newData[fieldIndex] = newData[fieldIndex - 1];
			newData[fieldIndex - 1] = temp;
			section.data = newData;
			setSections(newSections);
			triggerSave();
		}
	};

	const handleMoveFieldDown = (sectionIndex, fieldIndex) => {
		if (fieldIndex < sections[sectionIndex].data.length - 1) {
			const newSections = [...sections];
			const section = newSections[sectionIndex];
			const newData = [...section.data];
			const temp = newData[fieldIndex];
			newData[fieldIndex] = newData[fieldIndex + 1];
			newData[fieldIndex + 1] = temp;
			section.data = newData;
			setSections(newSections);
			triggerSave();
		}
	};

	const handleAddField = (sectionIndex) => {
		const newSections = [...sections];
		const section = newSections[sectionIndex];
		const newField = {
			id: Date.now(),
			type: "Text",
			options: {
				ref: "field_" + Date.now(),
				label: "New Field",
				placeholder: "Enter text..."
			}
		};
		section.data = [...section.data, newField];
		setSections(newSections);
		triggerSave();
	};

	const handleAddSection = () => {
		const newSections = [...sections];
		const newSection = {
			id: Date.now(),
			ref: "section_" + Date.now(),
			title: "New Section",
			description: "Section description",
			required: false,
			data: []
		};
		newSections.push(newSection);
		setSections(newSections);
		triggerSave();
	};

	const handleDeleteSection = (sectionIndex) => {
		if (confirm('Are you sure you want to delete this section and all its fields?')) {
			const newSections = [...sections];
			newSections.splice(sectionIndex, 1);
			setSections(newSections);
			triggerSave();
		}
	};

	const handleDeleteField = (sectionIndex, fieldIndex) => {
		if (confirm('Are you sure you want to delete this field?')) {
			const newSections = [...sections];
			const section = newSections[sectionIndex];
			section.data.splice(fieldIndex, 1);
			setSections(newSections);
			triggerSave();
		}
	};

	const triggerSave = () => {
		if (appsmith && appsmith.triggerEvent) {
			appsmith.triggerEvent("onSave", {
				sections: sections
			});
		}
	};

	const handleEditSave = () => {
		// Validate required fields
		if (editingItem.type === 'section') {
			if (!editFormData.title || !editFormData.ref) {
				alert('Title and reference are required');
				return;
			}
		} else if (editingItem.type === 'field') {
			if (!editFormData.label || !editFormData.type || !editFormData.ref) {
				alert('Label, type and reference are required');
				return;
			}
		}

		const newSections = [...sections];

		if (editingItem.type === 'section') {
			const section = newSections[editingItem.sectionIndex];
			section.title = editFormData.title;
			section.description = editFormData.description || '';
			section.ref = editFormData.ref;
		} else if (editingItem.type === 'field') {
			const section = newSections[editingItem.sectionIndex];
			const field = section.data[editingItem.fieldIndex];

			field.type = editFormData.type;
			field.options.label = editFormData.label;
			field.options.placeholder = editFormData.placeholder || '';
			field.options.ref = editFormData.ref;

			// Field-specific properties
			if (editFormData.type === 'Select') {
				field.options.options = editFormData.options?.split('\n').filter(opt => opt.trim()) || [];
			} else if (editFormData.type === 'Number') {
				field.options.maxValue = editFormData.maxValue;
				field.options.minValue = editFormData.minValue;
				field.options.numberOfDigits = editFormData.numberOfDigits;
				field.options.unit = editFormData.unit;
			} else if (editFormData.type === 'Textarea') {
				field.options.maxLength = editFormData.maxLength;
			} else if (editFormData.type === 'CheckBox') {
				field.options.options = editFormData.options?.split('\n').filter(opt => opt.trim()) || [];
				field.options.allowMultiple = editFormData.allowMultiple;
							} else if (editFormData.type === 'User selector') {
					field.options.roles = editFormData.roles || [];
					field.options.groups = editFormData.groups || [];
				}
		}

		setSections(newSections);
		triggerSave();
		setIsEditModalVisible(false);
		setEditingItem(null);
		setEditFormData({});
	};

	const handleEditCancel = () => {
		setIsEditModalVisible(false);
		setEditingItem(null);
		setEditFormData({});
