			<path d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z"/>
		</svg>
	);

	// Custom MultiSelect component with search and checkboxes
	const MultiSelect = ({ 
		options, 
		selectedValues, 
		onChange, 
		placeholder, 
		isHierarchical = false,
		groups = null 
	}) => {
		const [isOpen, setIsOpen] = React.useState(false);
		const [searchTerm, setSearchTerm] = React.useState('');
		const [localSelected, setLocalSelected] = React.useState(selectedValues || []);

		React.useEffect(() => {
			setLocalSelected(selectedValues || []);
		}, [selectedValues]);

		const filteredOptions = options.filter(option => 
			option.label.toLowerCase().includes(searchTerm.toLowerCase())
		);

		const handleToggle = (value) => {
			let newSelected;
			if (isHierarchical && groups) {
				// For hierarchical groups, include all children when parent is selected
				const childrenValues = getAllChildrenValues(groups, value);
				if (localSelected.includes(value)) {
					// Remove parent and all children
					newSelected = localSelected.filter(v => v !== value && !childrenValues.includes(v));
				} else {
					// Add parent and all children
					newSelected = [...localSelected, value, ...childrenValues];
				}
			} else {
				// For simple roles
				if (localSelected.includes(value)) {
					newSelected = localSelected.filter(v => v !== value);
				} else {
					newSelected = [...localSelected, value];
				}
			}
			setLocalSelected(newSelected);
			onChange(newSelected);
		};

		const selectedLabels = options
			.filter(option => localSelected.includes(option.value))
			.map(option => option.label);

		return (
			<div style={{ position: 'relative', width: '100%' }}>
				<div
					onClick={() => setIsOpen(!isOpen)}
					style={{
						border: '1px solid #d9d9d9',
						borderRadius: '6px',
						padding: '8px 12px',
						minHeight: '32px',
						cursor: 'pointer',
						backgroundColor: '#fff',
						display: 'flex',
						alignItems: 'center',
						justifyContent: 'space-between'
					}}
				>
					<div style={{ flex: 1, minHeight: '16px' }}>
						{selectedLabels.length > 0 ? (
							<div style={{ fontSize: '14px', color: '#333' }}>
								{selectedLabels.length} selected
							</div>
						) : (
							<div style={{ fontSize: '14px', color: '#999' }}>
								{placeholder}
							</div>
						)}
					</div>
					<div style={{ marginLeft: '8px' }}>
						{isOpen ? '▲' : '▼'}
					</div>
				</div>

				{isOpen && (
					<div style={{
						position: 'absolute',
						top: '100%',
						left: 0,
						right: 0,
						backgroundColor: '#fff',
						border: '1px solid #d9d9d9',
						borderRadius: '6px',
						boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
						zIndex: 1000,
						maxHeight: '200px',
						overflow: 'hidden'
