	};

	const renderFieldSummary = (field, sectionIndex, fieldIndex) => {
		const { type, id, options } = field;

		// Check if field is first or last in its section
		const isFirstField = fieldIndex === 0;
		const isLastField = fieldIndex === sections[sectionIndex].data.length - 1;

		// Hover state keys
		const gearKey = `field-gear-${sectionIndex}-${fieldIndex}`;
		const upKey = `field-up-${sectionIndex}-${fieldIndex}`;
		const downKey = `field-down-${sectionIndex}-${fieldIndex}`;
		const trashKey = `field-trash-${sectionIndex}-${fieldIndex}`;

		return (
			<List.Item key={id}>
				<div style={{ width: '100%', display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between' }}>
					<Space direction="vertical" style={{ flex: 1 }}>
						<Space>
							<Tag color="default">{type}</Tag>
							<Text strong>{options.label}</Text>
						</Space>
						<Text type="secondary" style={{ fontSize: '12px' }}>
							ID: {options.ref} | Placeholder: {options.placeholder}
							{options.options && options.options.length > 0 &&
								` | Options: ${options.options.join(', ')}`
							}
							{type === "User selector" && options.roles && options.roles.length > 0 &&
								` | Filter by roles: ${options.roles.join(', ')}`
							}
							{type === "User selector" && options.groups && options.groups.length > 0 &&
								` | Filter by groups: ${options.groups.join(', ')}`
							}
							{type === "Number" && options.maxValue &&
								` | Max: ${options.maxValue}`
							}
							{type === "Number" && options.minValue &&
								` | Min: ${options.minValue}`
							}
							{type === "Number" && options.numberOfDigits &&
								` | Digits: ${options.numberOfDigits}`
							}
							{type === "Number" && options.unit &&
								` | Unit: ${options.unit}`
							}
						</Text>
					</Space>
					<Space>
						<Button
							type="text"
							size="small"
							onClick={() => !isFirstField && handleMoveFieldUp(sectionIndex, fieldIndex)}
							onMouseEnter={() => !isFirstField && setHoverState(upKey, true)}
							onMouseLeave={() => setHoverState(upKey, false)}
							disabled={isFirstField}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								/*marginLeft: '0px',*/
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center',
								opacity: isFirstField ? 0.5 : 1
							}}
						>
							<ArrowUpIcon isHovered={hoverStates[upKey]} isDisabled={isFirstField} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => !isLastField && handleMoveFieldDown(sectionIndex, fieldIndex)}
							onMouseEnter={() => !isLastField && setHoverState(downKey, true)}
							onMouseLeave={() => setHoverState(downKey, false)}
							disabled={isLastField}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								/*marginLeft: '0px',*/
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center',
								opacity: isLastField ? 0.5 : 1
							}}
						>
							<ArrowDownIcon isHovered={hoverStates[downKey]} isDisabled={isLastField} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => handleFieldEdit(sectionIndex, fieldIndex, field)}
							onMouseEnter={() => setHoverState(gearKey, true)}
							onMouseLeave={() => setHoverState(gearKey, false)}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								/*marginLeft: '8px',*/
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center'
							}}
						>
							<GearIcon isHovered={hoverStates[gearKey]} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => handleDeleteField(sectionIndex, fieldIndex)}
							onMouseEnter={() => setHoverState(trashKey, true)}
							onMouseLeave={() => setHoverState(trashKey, false)}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center'
							}}
						>
							<TrashIcon isHovered={hoverStates[trashKey]} />
						</Button>
					</Space>
				</div>
			</List.Item>
		);
	};

	const renderSection = (section, sectionIndex) => {
		// Check if section is first or last
		const isFirstSection = sectionIndex === 0;
		const isLastSection = sectionIndex === sections.length - 1;

		// Hover state keys
		const gearKey = `section-gear-${sectionIndex}`;
		const upKey = `section-up-${sectionIndex}`;
		const downKey = `section-down-${sectionIndex}`;
		const trashKey = `section-trash-${sectionIndex}`;

		return (
			<Card 
				key={section.id} 
				style={{ marginBottom: 16 }}
				title={
					<div style={{ width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
						<Title level={4} style={{ margin: 0 }}>
							Section {sectionIndex + 1}: {section.title}
						</Title>
					</div>
				}
				extra={
					<Space>
						<Tag color="blue">{section.data.length} fields</Tag>
						<Text type="secondary">ID: {section.id}</Text>
						<Button
							type="text"
							size="small"
							onClick={() => !isFirstSection && handleMoveSectionUp(sectionIndex)}
							onMouseEnter={() => !isFirstSection && setHoverState(upKey, true)}
							onMouseLeave={() => setHoverState(upKey, false)}
							disabled={isFirstSection}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								marginLeft: '0px',
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center',
								opacity: isFirstSection ? 0.5 : 1
							}}
						>
							<ArrowUpIcon isHovered={hoverStates[upKey]} isDisabled={isFirstSection} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => !isLastSection && handleMoveSectionDown(sectionIndex)}
							onMouseEnter={() => !isLastSection && setHoverState(downKey, true)}
							onMouseLeave={() => setHoverState(downKey, false)}
							disabled={isLastSection}
							style={{
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								marginLeft: '0px',
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center',
								opacity: isLastSection ? 0.5 : 1
							}}
						>
							<ArrowDownIcon isHovered={hoverStates[downKey]} isDisabled={isLastSection} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => handleSectionEdit(sectionIndex, section)}
							onMouseEnter={() => setHoverState(gearKey, true)}
							onMouseLeave={() => setHoverState(gearKey, false)}
							style={{ 
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								marginLeft: '0px',
								marginRight: '8px',
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center'
							}}
						>
							<GearIcon isHovered={hoverStates[gearKey]} />
						</Button>
						<Button
							type="text"
							size="small"
							onClick={() => handleDeleteSection(sectionIndex)}
							onMouseEnter={() => setHoverState(trashKey, true)}
							onMouseLeave={() => setHoverState(trashKey, false)}
							style={{ 
								color: '#000000',
								padding: '2px 4px',
								minWidth: 'auto',
								border: 'none',
								boxShadow: 'none',
								marginRight: '8px',
								flexShrink: 0,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center'
							}}
						>
							<TrashIcon isHovered={hoverStates[trashKey]} />
						</Button>
					</Space>
				}
			>
				{section.description && (
					<Text type="secondary" style={{ display: 'block', marginBottom: 16 }}>
						{section.description}
					</Text>
				)}

				<List
					size="small"
					dataSource={section.data}
					renderItem={(field, fieldIndex) => renderFieldSummary(field, sectionIndex, fieldIndex)}
					style={{ backgroundColor: '#fafafa', borderRadius: 0, padding: 8 }}
				/>

				<div style={{ marginTop: 4, textAlign: 'center' }}>
					<Button
						type="dashed"
						size="small"
						onClick={() => handleAddField(sectionIndex)}
						style={{
							borderStyle: 'dashed',
							borderColor: '#d9d9d9',
							color: '#666666',
							width: '100%',
							padding: '2px 8px',
							height: '24px',
							fontSize: '11px'
						}}
					>
						Add a field
					</Button>
				</div>
			</Card>
		);
	};

	return (
		<div className="app">
			<div className="admin-container">
				<div className="sections-overview">
					{sections.map((section, index) => renderSection(section, index))}
					<div style={{ 
						marginTop: '16px', 
						textAlign: 'center',
						padding: '16px',
						border: '2px dashed #d9d9d9',
						borderRadius: '6px',
						backgroundColor: '#fafafa'
					}}>
						<Button
							type="dashed"
							size="large"
							onClick={handleAddSection}
							style={{
								borderStyle: 'dashed',
								borderColor: '#000',
								color: '#000',
								height: '40px',
								fontSize: '14px'
							}}
						>
							Add a section
						</Button>
					</div>
				</div>
			</div>

			{/* Edit Modal */}
			<Modal
				title={editingItem?.type === 'section' ? 'Edit Section' : 'Edit Field'}
				open={isEditModalVisible}
				onOk={handleEditSave}
				onCancel={handleEditCancel}
				width={600}
				okText="Save"
				cancelText="Cancel"
			>
				<div style={{ padding: '16px 0' }}>
					{editingItem?.type === 'section' ? (
						// Section edit form
						<Space direction="vertical" style={{ width: '100%' }}>
							<div>
								<Text strong>Title *</Text>
								<Input 
									value={editFormData.title || ''} 
									onChange={(e) => setEditFormData(prev => ({ ...prev, title: e.target.value }))}
									placeholder="Section title"
								/>
							</div>
							<div>
								<Text strong>Description</Text>
								<Input.TextArea 
									value={editFormData.description || ''} 
									onChange={(e) => setEditFormData(prev => ({ ...prev, description: e.target.value }))}
									placeholder="Section description"
									rows={3}
								/>
							</div>
							<div>
								<Text strong>Reference *</Text>
								<Input 
									value={editFormData.ref || ''} 
									onChange={(e) => setEditFormData(prev => ({ ...prev, ref: e.target.value }))}
									placeholder="section_1"
								/>
							</div>
						</Space>
					) : (
						// Field edit form
						<Space direction="vertical" style={{ width: '100%' }}>
							<Row gutter={16}>
								<Col span={12}>
									<div>
										<Text strong>Type *</Text>
										<select 
											value={editFormData.type || ''} 
											onChange={(e) => setEditFormData(prev => ({ ...prev, type: e.target.value }))}
											style={{ 
												width: '100%', 
												padding: '4px 8px', 
												border: '1px solid #d9d9d9', 
												borderRadius: '6px',
												fontSize: '14px'
											}}
										>
											<option value="">Select a type</option>
											<option value="Text">Text</option>
											<option value="Textarea">Textarea</option>
											<option value="Select">Select</option>
											<option value="Number">Number</option>
											<option value="Date">Date</option>
											<option value="CheckBox">CheckBox</option>
											<option value="User selector">User Autocomplete</option>
										</select>
									</div>
								</Col>
								<Col span={12}>
									<div>
										<Text strong>Reference *</Text>
										<Input 
											value={editFormData.ref || ''} 
											onChange={(e) => setEditFormData(prev => ({ ...prev, ref: e.target.value }))}
											placeholder="field_1"
										/>
									</div>
								</Col>
							</Row>
							<Row gutter={16}>
								<Col span={12}>
									<div>
										<Text strong>Label *</Text>
										<Input 
											value={editFormData.label || ''} 
											onChange={(e) => setEditFormData(prev => ({ ...prev, label: e.target.value }))}
											placeholder="Field name"
										/>
									</div>
								</Col>
								<Col span={12}>
									<div>
										<Text strong>Placeholder</Text>
										<Input 
											value={editFormData.placeholder || ''} 
											onChange={(e) => setEditFormData(prev => ({ ...prev, placeholder: e.target.value }))}
											placeholder="Help text"
										/>
									</div>
								</Col>
							</Row>

							{/* Field-specific properties */}
							{editFormData.type === 'Select' && (
								<div>
									<Text strong>Options (one per line)</Text>
									<Input.TextArea 
										value={editFormData.options || ''} 
										onChange={(e) => setEditFormData(prev => ({ ...prev, options: e.target.value }))}
										placeholder="Option 1&#10;Option 2&#10;Option 3"
										rows={4}
									/>
								</div>
							)}

							{editFormData.type === 'Number' && (
								<Row gutter={16}>
									<Col span={6}>
										<div>
											<Text strong>Min value</Text>
											<Input 
												type="number"
												value={editFormData.minValue || ''} 
												onChange={(e) => setEditFormData(prev => ({ ...prev, minValue: e.target.value }))}
											/>
										</div>
									</Col>
									<Col span={6}>
										<div>
											<Text strong>Max value</Text>
											<Input 
												type="number"
												value={editFormData.maxValue || ''} 
												onChange={(e) => setEditFormData(prev => ({ ...prev, maxValue: e.target.value }))}
											/>
										</div>
									</Col>
									<Col span={6}>
										<div>
											<Text strong>Decimals</Text>
											<Input 
												type="number"
												min={0}
												max={10}
												value={editFormData.numberOfDigits || ''} 
												onChange={(e) => setEditFormData(prev => ({ ...prev, numberOfDigits: e.target.value }))}
											/>
										</div>
									</Col>
									<Col span={6}>
										<div>
											<Text strong>Unit</Text>
											<Input 
												value={editFormData.unit || ''} 
												onChange={(e) => setEditFormData(prev => ({ ...prev, unit: e.target.value }))}
												placeholder="€, $, cm..."
											/>
										</div>
									</Col>
								</Row>
							)}

							{editFormData.type === 'Textarea' && (
								<div>
									<Text strong>Max length</Text>
									<Input 
										type="number"
										min={1}
										value={editFormData.maxLength || ''} 
										onChange={(e) => setEditFormData(prev => ({ ...prev, maxLength: e.target.value }))}
									/>
								</div>
							)}

							{editFormData.type === 'CheckBox' && (
								<Space direction="vertical" style={{ width: '100%' }}>
									<div>
										<Text strong>Options (one per line)</Text>
										<Input.TextArea 
											value={editFormData.options || ''} 
											onChange={(e) => setEditFormData(prev => ({ ...prev, options: e.target.value }))}
											placeholder="Option 1&#10;Option 2&#10;Option 3"
											rows={4}
										/>
									</div>
									<div>
										<Text strong>Multiple selection</Text>
										<Switch 
											checked={editFormData.allowMultiple || false} 
											onChange={(checked) => setEditFormData(prev => ({ ...prev, allowMultiple: checked }))}
										/>
									</div>
								</Space>
							)}

							{editFormData.type === 'User selector' && (
								<Row gutter={16}>
									<Col span={12}>
										<div>
											<Text strong>Filter by roles (optional)</Text>
											<MultiSelect
												options={appsmith?.model?.roles || []}
												selectedValues={editFormData.roles || []}
												onChange={(values) => setEditFormData(prev => ({ ...prev, roles: values }))}
												placeholder="Select roles..."
												isHierarchical={false}
											/>
											<Text type="secondary" style={{ fontSize: '12px', marginTop: '4px' }}>
												Select roles to filter users
											</Text>
										</div>
									</Col>
									<Col span={12}>
										<div>
											<Text strong>Filter by groups (optional)</Text>
											<MultiSelect
												options={appsmith?.model?.groups ? flattenGroups(appsmith.model.groups) : []}
												selectedValues={editFormData.groups || []}
												onChange={(values) => setEditFormData(prev => ({ ...prev, groups: values }))}
												placeholder="Select groups..."
												isHierarchical={true}
												groups={appsmith?.model?.groups}
											/>
											<Text type="secondary" style={{ fontSize: '12px', marginTop: '4px' }}>
												Select groups to filter users (children auto-selected)
											</Text>
										</div>
									</Col>
								</Row>
							)}
						</Space>
					)}
				</div>
			</Modal>
		</div>
	);
}

appsmith.onReady(() => {
	ReactDOM.render(<App />, document.getElementById("root"));
});

