// Import React, ReactDOM and Ant Design components
import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'

function App() {
	const [rows, setRows] = React.useState([{ id: 1, group: null, role: null }]);
	const [antdLoaded, setAntdLoaded] = React.useState(false);
	const [isReady, setIsReady] = React.useState(false);

	// Check if Ant Design is loaded
	React.useEffect(() => {
		if (Button && Card) {
			setAntdLoaded(true);
		}
	}, []);

	// Wait for appsmith to be ready
	React.useEffect(() => {
		const checkAppsmith = () => {
			if (typeof appsmith !== 'undefined' && appsmith.model) {
				setIsReady(true);
			} else {
				setTimeout(checkAppsmith, 100);
			}
		};
		checkAppsmith();
	}, []);

	// Get groups and roles from appsmith.model safely
	const getGroups = () => {
		try {
			return Array.isArray(appsmith?.model?.groups) ? appsmith.model.groups : [];
		} catch (error) {
			console.error('Error accessing groups:', error);
			return [];
		}
	};

	const getRoles = () => {
		try {
			return Array.isArray(appsmith?.model?.roles) ? appsmith.model.roles : [];
		} catch (error) {
			console.error('Error accessing roles:', error);
			return [];
		}
	};

	// Flatten groups for easier selection with hierarchical display
	const getFlattenedGroups = () => {
		try {
			const groups = getGroups();
			if (!Array.isArray(groups)) return [];

			const result = [];

			const flatten = (items, level = 0) => {
				if (!Array.isArray(items)) return;

				items.forEach(item => {
					if (!item || typeof item !== 'object') return;

					// Create indentation based on level
					const indent = '　'.repeat(level * 2) + "- "; // Using full-width space for better alignment
					const label = indent + (item.label || item.value || '');

					result.push({
						label: label,
						value: item.value || item.key || '',
						level: level
					});

					if (item.children && Array.isArray(item.children)) {
						flatten(item.children, level + 1);
					}
				});
			};

			flatten(groups);
			return result;
		} catch (error) {
			console.error('Error flattening groups:', error);
			return [];
		}
	};

	// Add new row
	const handleAddRow = () => {
		const newId = Date.now(); 
		setRows([...rows, { id: newId, group: null, role: null }]);
	};

	// Remove row (but keep first row)
	const handleRemoveRow = (id) => {
		if (rows.length > 1) {
			setRows(rows.filter(row => row.id !== id));
		}
	};

	// Update row values
	const handleGroupChange = (id, value) => {
		setRows(rows.map(row => 
			row.id === id ? { ...row, group: value } : row
		));
	};

	const handleRoleChange = (id, value) => {
		setRows(rows.map(row => 
			row.id === id ? { ...row, role: value } : row
		));
	};

	// Show loading if not ready
	if (!isReady || !antdLoaded) {
		return (
			<div className="membership-widget">
				<div style={{ textAlign: 'center', padding: '50px' }}>
					<div>Loading...</div>
				</div>
			</div>
		);
	}

	const flattenedGroups = getFlattenedGroups();
	const roles = getRoles();

	return (
		<div className="membership-widget">
			<Card style={{ border: 'none', boxShadow: 'none' }}>
				{rows.map((row, index) => (
					<div key={row.id} style={{ 
						display: 'flex', 
						alignItems: 'center', 
						gap: '10px', 
						marginBottom: '10px',
						padding: '10px'
					}}>
						<div style={{ flex: 1 }}>
							<select
								value={row.group || ''}
								onChange={(e) => handleGroupChange(row.id, e.target.value || null)}
								style={{ 
									width: '100%', 
									padding: '8px 12px',
									border: '1px solid #d9d9d9',
									borderRadius: '0',
									fontSize: '14px',
									backgroundColor: 'white'
								}}
							>
								<option value="">Select group (optional)</option>
								{flattenedGroups.map(group => (
									<option key={group.value} value={group.value}>
										{group.label}
									</option>
								))}
							</select>
						</div>

						<div style={{ flex: 1 }}>
							<select
								value={row.role || ''}
								onChange={(e) => handleRoleChange(row.id, e.target.value || null)}
								style={{ 
									width: '100%', 
									padding: '8px 12px',
									border: '1px solid #d9d9d9',
									borderRadius: '0',
									fontSize: '14px',
									backgroundColor: 'white'
								}}
							>
								<option value="">Select role (optional)</option>
								{roles.map(role => (
									<option key={role.value} value={role.value}>
										{role.label}
									</option>
								))}
							</select>
						</div>

						{rows.length > 1 && (
							<Button 
								size="small" 
								danger 
								onClick={() => handleRemoveRow(row.id)}
								style={{ minWidth: '60px', borderRadius: '0' }}
							>
								Remove
							</Button>
						)}
					</div>
				))}

				<div style={{ textAlign: 'center', marginTop: '20px' }}>
					<Button 
						type="primary" 
						onClick={handleAddRow}
						style={{ 
							padding: '8px 16px',
							height: 'auto',
							backgroundColor: '#1e3a8a',
							borderRadius: '0'
						}}
					>
						+ Add Membership
					</Button>
				</div>
			</Card>
		</div>
	);
}

appsmith.onReady(() => {
	reactDOM.render(<App />, document.getElementById("root"));
});

