            transform: translateY(-50%);
            display: flex;
            flex-direction: column;
            gap: 4px;
            opacity: 0;
            transition: opacity 0.3s ease;

            .move-btn {
              width: 24px;
              height: 24px;
              border: none;
              background: rgba(30, 58, 138, 0.1);
              border-radius: 4px;
              cursor: pointer;
              display: flex;
              align-items: center;
              justify-content: center;
              transition: all 0.3s ease;
              color: #1e3a8a;

              &:hover {
                background: rgba(30, 58, 138, 0.2);
                transform: scale(1.1);
              }

              svg {
                filter: drop-shadow(0 1px 2px rgba(30, 58, 138, 0.1));
              }
            }
          }

          &.active .step-actions .move-btn {
            color: #1e3a8a;
            background: rgba(255, 255, 255, 0.2);

            &:hover {
              background: rgba(255, 255, 255, 0.3);
            }
          }

          &:hover .step-actions {
            opacity: 1;
          }
			}

			.flow-arrow {
				display: flex;
				align-items: center;
				justify-content: center;
				transition: all 0.3s ease;
				height: 40px;

				&.vertical-arrow {
					svg {
						filter: drop-shadow(0 1px 2px rgba(30, 58, 138, 0.1));
					}
				}
			}

			.add-step-button {
				display: flex;
				flex-direction: column;
				align-items: center;
				padding: 16px 12px;
				background: #f8fafc;
				border-radius: 0px;
				cursor: pointer;
				transition: all 0.3s ease;
				border: 2px dashed #1e3a8a;
				position: relative;
				width: 90%;
				height: 100px;
				text-align: center;
				box-shadow: 0 2px 4px rgba(30, 58, 138, 0.1);

				&:hover {
					transform: translateY(-2px);
					box-shadow: 0 8px 20px rgba(30, 58, 138, 0.15);
					border-color: #1e3a8a;
					background: rgba(30, 58, 138, 0.05);
				}

				        .add-icon {
          width: 50px;
          height: 50px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          background: #1e3a8a;
          margin-bottom: 12px;
          transition: all 0.3s ease;
          border: none;
        }

				.add-text {
					font-weight: 600;
					font-size: 12px;
					color: #1e3a8a;
					line-height: 1.3;
				}
			}
		}
		.bpmn-container::after {
			content: "";
			display: block;
			height: 20px; /* ou une valeur suffisante pour forcer Appsmith à étendre */
		}
	}
}

/* Animations */
@keyframes bounceIn {
	0% { transform: scale(0); }
	50% { transform: scale(1.2); }
	100% { transform: scale(1); }
}

