import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'
import Markdown from 'https://cdn.jsdelivr.net/npm/react-markdown@9.0.1/+esm';

function App() {
	const [currentStep, setCurrentStep] = React.useState(0);

	const steps = appsmith.model.steps;

	const handleAddStep = () => {
		appsmith.triggerEvent("onAddStepClick");
	};

	const handleStepClick = (step, index) => {
		setCurrentStep(index);
		appsmith.triggerEvent("onStepClick", {
			step: step,
			stepIndex: index
		});
	};

	const handleMoveStep = (direction, index) => {
		appsmith.triggerEvent("onMoveStep", {
			stepIndex: index,
			direction: direction // 'up' ou 'down'
		});
	};

	return (
		<Card className="app">
		<div className="bpmn-container">
		<div className="bpmn-flow">
		{steps.map((step, index) => (
		<React.Fragment key={step.id}>
		<div 
className={`bpmn-step ${index === currentStep ? 'active' : ''} ${index < currentStep ? 'completed' : ''} ${index > currentStep ? 'pending' : ''}`}
								onClick={() => handleStepClick(step, index)}
							>
								<div className="step-icon">
									{index + 1}
								</div>
								<div className="step-content">
									<div className="step-title">{step.name}</div>
									<div className="step-ref">{step.ref}</div>
								</div>
								<div className="step-actions">
									{index > 0 && (
										<button 
											className="move-btn move-up"
											onClick={(e) => {
												e.stopPropagation();
												handleMoveStep('up', index);
											}}
											title="Déplacer vers le haut"
										>
											<svg width="12" height="12" viewBox="0 0 12 12" fill="none">
												<path d="M6 2L2 6L6 10" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" transform="rotate(90 6 6)"/>
											</svg>
										</button>
									)}
									{index < steps.length - 1 && (
										<button 
											className="move-btn move-down"
											onClick={(e) => {
												e.stopPropagation();
												handleMoveStep('down', index);
											}}
											title="Déplacer vers le bas"
										>
											<svg width="12" height="12" viewBox="0 0 12 12" fill="none">
												<path d="M6 2L2 6L6 10" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" transform="rotate(-90 6 6)"/>
											</svg>
										</button>
									)}
								</div>
							</div>

							{index < steps.length - 1 && (
								<div className="flow-arrow vertical-arrow">
									<svg width="20" height="40" viewBox="0 0 20 40">
										<defs>
											<linearGradient id={`arrowGradient${index}`} x1="0%" y1="0%" x2="0%" y2="100%">
												<stop offset="0%" stopColor="#1e3a8a" />
												<stop offset="100%" stopColor="#1e3a8a" />
											</linearGradient>
										</defs>
										<path 
											d="M10 5 L10 35 M5 30 L10 35 L15 30" 
											stroke="url(#arrowGradient0)" 
											strokeWidth="2" 
											fill="none"
										/>
									</svg>
								</div>
							)}
						</React.Fragment>
					))}

					{/* Flèche après la dernière étape */}
					<div className="flow-arrow vertical-arrow">
						<svg width="20" height="40" viewBox="0 0 20 40">
							<defs>
								<linearGradient id="finalArrowGradient" x1="0%" y1="0%" x2="0%" y2="100%">
									<stop offset="0%" stopColor="#1e3a8a" />
									<stop offset="100%" stopColor="#1e3a8a" />
								</linearGradient>
							</defs>
							<path 
								d="M10 5 L10 35 M5 30 L10 35 L15 30" 
								stroke="url(#finalArrowGradient)" 
								strokeWidth="2" 
								fill="none"
							/>
						</svg>
					</div>

					{/* Bouton d'ajout d'étape */}
					<div className="add-step-button" onClick={handleAddStep}>
						<div className="add-icon">
							<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
								<path d="M12 4V20M4 12H20" stroke="white" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
							</svg>
						</div>
						<div className="add-text">Add a step</div>
					</div>
				</div>
			</div>
		</Card>
	);
}

appsmith.onReady(() => {
	/*
