import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Card, Switch, Checkbox, Collapse, Space, Typography, Tag } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'

const { Panel } = Collapse;
const { Title, Text } = Typography;

function App() {
	const [formConfig, setFormConfig] = React.useState({});
	const sections = appsmith.model.sections || [];

	// Initialize form configuration when component mounts
	React.useEffect(() => {
		const initialConfig = {};
		sections.forEach(section => {
			initialConfig[section.id] = {
				visible: true,
				fields: {}
			};
			section.data.forEach(field => {
				initialConfig[section.id].fields[field.id] = {
					visible: true,
					readOnly: false,
					required: false
				};
			});
		});
		setFormConfig(initialConfig);
	}, [sections]);

	const handleSectionVisibilityChange = (sectionId, visible) => {
		setFormConfig(prev => ({
			...prev,
			[sectionId]: {
				...prev[sectionId],
				visible: visible,
				fields: Object.keys(prev[sectionId]?.fields || {}).reduce((acc, fieldId) => {
					acc[fieldId] = {
						...prev[sectionId].fields[fieldId],
						visible: visible // If section is hidden, all fields are hidden
					};
					return acc;
				}, {})
			}
		}));
	};

	const handleFieldVisibilityChange = (sectionId, fieldId, visible) => {
		setFormConfig(prev => ({
			...prev,
			[sectionId]: {
				...prev[sectionId],
				fields: {
					...prev[sectionId].fields,
					[fieldId]: {
						...prev[sectionId].fields[fieldId],
						visible: visible
					}
				}
			}
		}));
	};

	const handleFieldReadOnlyChange = (sectionId, fieldId, readOnly) => {
		setFormConfig(prev => ({
			...prev,
			[sectionId]: {
				...prev[sectionId],
				fields: {
					...prev[sectionId].fields,
					[fieldId]: {
						...prev[sectionId].fields[fieldId],
						readOnly: readOnly
					}
				}
			}
		}));
	};

	const handleFieldRequiredChange = (sectionId, fieldId, required) => {
		setFormConfig(prev => ({
			...prev,
			[sectionId]: {
				...prev[sectionId],
				fields: {
					...prev[sectionId].fields,
					[fieldId]: {
						...prev[sectionId].fields[fieldId],
						required: required
					}
				}
			}
		}));
	};



	const getFieldTypeColor = (type) => {
		return 'default'; // Tous les tags en gris
	};

	return (
		<Card className="form-config-widget">
			<div className="sections-container">
				<Collapse defaultActiveKey={sections.map(s => s.id)} ghost>
					{sections.map(section => (
						<Panel 
							header={
								<div className="section-header">
									<Space>
										<Switch
											checked={formConfig[section.id]?.visible || false}
											onChange={(checked) => handleSectionVisibilityChange(section.id, checked)}
											onClick={(e) => e.stopPropagation()}
										/>
										<span className="section-title">{section.title}</span>
										{!formConfig[section.id]?.visible && (
											<Text type="secondary" italic>(Hidden)</Text>
										)}
									</Space>
								</div>
							}
							key={section.id}
							className={!formConfig[section.id]?.visible ? 'hidden-section' : ''}
						>
							<div className="section-description">
								<Text type="secondary">{section.description}</Text>
							</div>

							<div className="fields-container">
								{section.data.map(field => (
									<div key={field.id} className="field-config-item">
										<div className="field-header">
											<Space align="start">
												<Tag color={getFieldTypeColor(field.type)}>{field.type}</Tag>
												<div className="field-info">
													<Text strong>{field.options.label}</Text>
													<Text type="secondary" className="field-ref">{field.options.ref}</Text>
												</div>
											</Space>
										</div>

										<div className="field-controls">
											<Space direction="vertical" size="small">
												<div className="control-row">
													<Text>Visible:</Text>
													<Switch
														checked={formConfig[section.id]?.fields[field.id]?.visible || false}
														onChange={(checked) => handleFieldVisibilityChange(section.id, field.id, checked)}
														disabled={!formConfig[section.id]?.visible}
													/>
												</div>

												{formConfig[section.id]?.fields[field.id]?.visible && (
													<>
														<div className="control-row">
															<Text>Read Only:</Text>
															<Switch
																checked={formConfig[section.id]?.fields[field.id]?.readOnly || false}
																onChange={(checked) => handleFieldReadOnlyChange(section.id, field.id, checked)}
															/>
														</div>

														{!formConfig[section.id]?.fields[field.id]?.readOnly && (
															<div className="control-row">
																<Text>Required:</Text>
																<Switch
																	checked={formConfig[section.id]?.fields[field.id]?.required || false}
																	onChange={(checked) => handleFieldRequiredChange(section.id, field.id, checked)}
																/>
															</div>
														)}
													</>
												)}
											</Space>
										</div>
									</div>
								))}
							</div>
						</Panel>
					))}
				</Collapse>
			</div>
		</Card>
	);
}

appsmith.onReady(() => {
