.app .case-list-container .case-list .case-list-item .case-expanded-details .case-dates .date-item .date-label {
  font-size: 12px;
  color: #64748b;
  font-weight: 500;
}
.app .case-list-container .case-list .case-list-item .case-expanded-details .case-dates .date-item .date-value {
  font-size: 12px;
  color: #1e3a8a;
  font-weight: 600;
}
.app .case-list-container .add-case-button {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 40px 20px;
  background: #f8fafc;
  border: 2px dashed #1e3a8a;
  border-radius: 0px;
  cursor: pointer;
  transition: all 0.3s ease;
  width: 100%;
  max-width: 400px;
  margin: 0 auto;
}
.app .case-list-container .add-case-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(30, 58, 138, 0.15);
  border-color: #1e3a8a;
  background: rgba(30, 58, 138, 0.05);
}
.app .case-list-container .add-case-button .add-icon {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #1e3a8a;
  margin-bottom: 12px;
  transition: all 0.3s ease;
}
.app .case-list-container .add-case-button .add-text {
  font-weight: 600;
  font-size: 14px;
  color: #1e3a8a;
  line-height: 1.3;
}

/* Responsive */
@media (max-width: 768px) {
  .app .case-list-container {
    padding: 16px;
  }
  .app .case-list-container .kpi-section .kpi-grid {
    grid-template-columns: repeat(2, 1fr);
    gap: 12px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card {
    padding: 16px;
    gap: 12px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-icon {
    width: 40px;
    height: 40px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-content .kpi-value {
    font-size: 20px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-content .kpi-label {
    font-size: 12px;
  }
  .app .case-list-container .case-header {
    flex-direction: column;
    gap: 16px;
    align-items: stretch;
  }
  .app .case-list-container .case-header .header-right {
    flex-direction: column;
    gap: 8px;
  }
  .app .case-list-container .case-header .header-right .search-input,
  .app .case-list-container .case-header .header-right .status-filter {
    width: 100%;
  }
  .app .case-list-container .case-list .case-list-item .case-list-header {
    flex-direction: column;
    align-items: stretch;
    gap: 12px;
  }
  .app .case-list-container .case-list .case-list-item .case-list-header .case-basic-info .case-id-status {
    justify-content: space-between;
  }
  .app .case-list-container .case-list .case-list-item .case-list-header .case-progress-info {
    min-width: auto;
  }
  .app .case-list-container .case-list .case-list-item .case-list-header .case-actions {
    justify-content: center;
  }
  .app .case-list-container .case-list .case-list-item .case-expanded-details .steps-summary {
    grid-template-columns: 1fr;
  }
  .app .case-list-container .case-list .case-list-item .case-expanded-details .steps-history .steps-timeline .step-timeline-item .step-timeline-content .step-timeline-header {
    flex-direction: column;
    align-items: flex-start;
    gap: 4px;
  }
  .app .case-list-container .case-list .case-list-item .case-expanded-details .steps-history .steps-timeline .step-timeline-item .step-timeline-content .step-timeline-details {
    flex-direction: column;
    gap: 4px;
  }
  .app .case-list-container .case-list .case-list-item .case-expanded-details .case-dates {
    grid-template-columns: 1fr;
  }
}
@media (max-width: 480px) {
  .app .case-list-container .kpi-section .kpi-grid {
    grid-template-columns: repeat(2, 1fr);
    gap: 8px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card {
    padding: 12px;
    gap: 8px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-icon {
    width: 32px;
    height: 32px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-content .kpi-value {
    font-size: 18px;
  }
  .app .case-list-container .kpi-section .kpi-grid .kpi-card .kpi-content .kpi-label {
    font-size: 11px;
  }
}
/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.case-list-item {
  animation: fadeIn 0.3s ease-out;
}

@keyframes expand {
  from {
    max-height: 0;
    opacity: 0;
  }
  to {
    max-height: 500px;
    opacity: 1;
  }
}
.case-expanded-details {
  animation: expand 0.3s ease-out;
}

