import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card, Badge, Tooltip, Progress, Collapse } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'
import Markdown from 'https://cdn.jsdelivr.net/npm/react-markdown@9.0.1/+esm';

function App() {
	const [selectedCase, setSelectedCase] = React.useState(null);
	const [searchTerm, setSearchTerm] = React.useState('');
	const [statusFilter, setStatusFilter] = React.useState('all');
	const [expandedCases, setExpandedCases] = React.useState(new Set());

	const cases = appsmith.model.cases || [];

	// Filtrer les cas
	const filteredCases = cases.filter(caseItem => {
		const matchesSearch = searchTerm === '' || 
			caseItem.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.processName.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.assignedUser.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.assignedUser.lastName.toLowerCase().includes(searchTerm.toLowerCase());

		const matchesStatus = statusFilter === 'all' || statusFilter === '' || caseItem.status === statusFilter;

		return matchesSearch && matchesStatus;
	});

	const handleCaseClick = (caseItem) => {
		setSelectedCase(caseItem);
		appsmith.triggerEvent("onCaseClick", {
			case: caseItem
		});
	};



	const handleEditCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onEditCaseClick", {
			case: caseItem
		});
	};

	const handleCloseCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onCloseCaseClick", {
			case: caseItem
		});
	};

	const handleAssignCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onAssignCaseClick", {
			case: caseItem
		});
	};

	const toggleCaseExpansion = (caseId, event) => {
		event.stopPropagation();
		const newExpanded = new Set(expandedCases);
		if (newExpanded.has(caseId)) {
			newExpanded.delete(caseId);
		} else {
			newExpanded.add(caseId);
		}
		setExpandedCases(newExpanded);
	};

	const getStatusColor = (status) => {
		switch (status) {
			case 'completed': return 'success';
			case 'in_progress': return 'processing';
			case 'pending': return 'warning';
			case 'error': return 'error';
			case 'cancelled': return 'error';
			default: return 'default';
		}
	};

	const getStatusText = (status) => {
		switch (status) {
			case 'completed': return 'Completed';
			case 'in_progress': return 'In Progress';
			case 'pending': return 'Pending';
			case 'error': return 'Error';
			case 'cancelled': return 'Cancelled';
			default: return 'Unknown';
		}
	};

	const formatDate = (dateString) => {
		if (!dateString) return '-';
		return new Date(dateString).toLocaleDateString('en-US', {
			day: '2-digit',
			month: '2-digit',
			year: 'numeric',
			hour: '2-digit',
			minute: '2-digit'
		});
	};

	const getCurrentStepInfo = (caseItem) => {
		const currentStep = caseItem.steps.find(step => step.status === 'in_progress');
		if (currentStep) {
			return {
				name: currentStep.stepName,
				assignedTo: currentStep.assignedTo,
				startDate: currentStep.startDate
			};
		}
		return null;
	};

	const getCompletedStepsCount = (caseItem) => {
		return caseItem.steps.filter(step => step.status === 'completed').length;
	};

	const getPendingStepsCount = (caseItem) => {
		return caseItem.steps.filter(step => step.status === 'pending').length;
	};

	const getStepStatusIcon = (status) => {
		switch (status) {
			case 'completed':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<path d="M20 6L9 17l-5-5" stroke="#15803d" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'in_progress':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#3b82f6" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#3b82f6" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'pending':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#d97706" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#d97706" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'error':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#f5222d" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#f5222d" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			default:
				return null;
		}
	};

	const getSummaryDate = (caseItem) => {
		if (caseItem.status === 'completed' && caseItem.completionDate) {
			return {
				label: 'Completed',
				date: caseItem.completionDate,
				color: '#15803d'
			};
		} else {
			const currentStepInfo = getCurrentStepInfo(caseItem);
			if (currentStepInfo && currentStepInfo.startDate) {
				return {
					label: 'Current Step Started',
					date: currentStepInfo.startDate,
					color: '#3b82f6'
				};
			}
		}
		return null;
	};

	// Calculate KPIs
	const calculateKPIs = (cases) => {
		const totalCases = cases.length;
		const completedCases = cases.filter(c => c.status === 'completed').length;
		const inProgressCases = cases.filter(c => c.status === 'in_progress').length;
		const errorCases = cases.filter(c => c.status === 'error').length;
		const cancelledCases = cases.filter(c => c.status === 'cancelled').length;
		const completionRate = totalCases > 0 ? Math.round((completedCases / totalCases) * 100) : 0;
		const averageCompletionPercentage = cases.length > 0 
			? Math.round(cases.reduce((sum, c) => sum + (c.completionPercentage || 0), 0) / cases.length)
			: 0;

		return {
			totalCases,
			completedCases,
			inProgressCases,
			errorCases,
			cancelledCases,
			completionRate,
			averageCompletionPercentage
		};
	};

	const kpis = calculateKPIs(cases);

	return (
		<Card className="app">
			<div className="case-list-container">
				{/* KPI Section */}
				<div className="kpi-section">
					<div className="kpi-grid">
						<div className="kpi-card total">
							<div className="kpi-icon">
								<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
									<path d="M9 12l2 2 4-4M21 12c0 4.97-4.03 9-9 9s-9-4.03-9-9 4.03-9 9-9 9 4.03 9 9z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
							</div>
							<div className="kpi-content">
								<div className="kpi-value">{kpis.totalCases}</div>
								<div className="kpi-label">Total Cases</div>
							</div>
						</div>

						<div className="kpi-card completed">
							<div className="kpi-icon">
								<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
									<path d="M20 6L9 17l-5-5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
							</div>
							<div className="kpi-content">
								<div className="kpi-value">{kpis.completedCases}</div>
								<div className="kpi-label">Completed</div>
								<div className="kpi-percentage">{kpis.completionRate}%</div>
							</div>
						</div>

						<div className="kpi-card in-progress">
							<div className="kpi-icon">
								<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
									<circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
									<path d="M12 6v6l4 2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
							</div>
							<div className="kpi-content">
								<div className="kpi-value">{kpis.inProgressCases}</div>
								<div className="kpi-label">In Progress</div>
							</div>
						</div>

						<div className="kpi-card error">
							<div className="kpi-icon">
								<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
									<circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
									<path d="M12 8v4M12 16h.01" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
							</div>
							<div className="kpi-content">
								<div className="kpi-value">{kpis.errorCases}</div>
								<div className="kpi-label">Error</div>
							</div>
						</div>

						<div className="kpi-card cancelled">
							<div className="kpi-icon">
								<svg width="24" height="24" viewBox="0 0 24 24" fill="none">
									<path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
							</div>
							<div className="kpi-content">
								<div className="kpi-value">{kpis.cancelledCases}</div>
								<div className="kpi-label">Cancelled</div>
							</div>
						</div>
					</div>
				</div>

				{/* Header with filters */}
				<div className="case-header">
					<div className="header-left">
						<h3>Case List</h3>
						<span className="case-count">{filteredCases.length} cases</span>
					</div>
					<div className="header-right">
						<input
							type="text"
							placeholder="Search for a case..."
							value={searchTerm}
							onChange={(e) => setSearchTerm(e.target.value)}
							className="search-input"
						/>
						<select
							value={statusFilter}
							onChange={(e) => setStatusFilter(e.target.value)}
							className="status-filter"
						>
							<option value="all">All statuses</option>
							<option value="completed">Completed</option>
							<option value="in_progress">In Progress</option>
							<option value="error">Error</option>
							<option value="cancelled">Cancelled</option>
						</select>
					</div>
				</div>

				{/* Case List */}
				<div className="case-list">
					{filteredCases.map((caseItem) => {
						const currentStepInfo = getCurrentStepInfo(caseItem);
						const completedSteps = getCompletedStepsCount(caseItem);
						const pendingSteps = getPendingStepsCount(caseItem);
						const isExpanded = expandedCases.has(caseItem.id);

						return (
							<div
								key={caseItem.id}
								className={`case-list-item ${selectedCase?.id === caseItem.id ? 'selected' : ''}`}
								onClick={() => handleCaseClick(caseItem)}
							>
								{/* Case Header Row */}
								<div className="case-list-header">
									<div className="case-basic-info">
										<div className="case-id-status">
											<h4 className="case-id">{caseItem.id}</h4>
											<Badge 
												status={getStatusColor(caseItem.status)} 
												text={getStatusText(caseItem.status)}
												className="status-badge"
											/>
										</div>
										<div className="case-details">
											<span className="process-name">{caseItem.processName}</span>
											<span className="assigned-user">
												Initiated by: {caseItem.assignedUser.firstName} {caseItem.assignedUser.lastName}
											</span>
											<div className="dates-line">
												<span className="case-start-date">
													Started: {formatDate(caseItem.startDate)}
												</span>
												{(() => {
													const summaryDate = getSummaryDate(caseItem);
													return summaryDate ? (
														<>
															<span className="date-separator">•</span>
															<span className="summary-date" style={{ color: summaryDate.color }}>
																{summaryDate.label}: {formatDate(summaryDate.date)}
															</span>
														</>
													) : null;
												})()}
											</div>
										</div>
									</div>

									<div className="case-progress-info">
										<div className="progress-mini">
											<span className="progress-text">{caseItem.completionPercentage}%</span>
											<Progress 
												percent={caseItem.completionPercentage} 
												status={caseItem.status === 'completed' ? 'success' : 'active'}
												strokeColor={caseItem.status === 'completed' ? '#15803d' : '#1890ff'}
												showInfo={false}
												size="small"
												className="progress-bar-mini"
											/>
										</div>
									</div>

									<div className="case-actions">
										{caseItem.status !== 'completed' && (
											<Tooltip title="Modify Data">
												<button
													className="action-btn edit-btn"
													onClick={(e) => handleEditCase(caseItem, e)}
												>
													<svg width="14" height="14" viewBox="0 0 24 24" fill="none">
														<path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
														<path d="m18.5 2.5 3 3L12 15l-4 1 1-4 9.5-9.5z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
													</svg>
												</button>
											</Tooltip>
										)}

										{caseItem.status !== 'completed' && (
											<Tooltip title="Close">
												<button
													className="action-btn close-btn"
													onClick={(e) => handleCloseCase(caseItem, e)}
												>
													<svg width="14" height="14" viewBox="0 0 24 24" fill="none">
														<path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
													</svg>
												</button>
											</Tooltip>
										)}

										<button
											className={`expand-btn ${isExpanded ? 'expanded' : ''}`}
											onClick={(e) => toggleCaseExpansion(caseItem.id, e)}
										>
											<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
												<path d="M6 9l6 6 6-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
											</svg>
										</button>
									</div>
								</div>

								{/* Expanded Details */}
								{isExpanded && (
									<div className="case-expanded-details">
										{/* Current Step Info */}
										{currentStepInfo && (
											<div className="current-step">
												<div className="current-step-header">
													<div className="current-step-info">
														<span className="current-step-label">Current Step:</span>
														<span className="current-step-name">{currentStepInfo.name}</span>
														<span className="current-step-assignee">({currentStepInfo.assignedTo})</span>
													</div>
													{caseItem.status !== 'completed' && (
														<Tooltip title="Assign Current Step">
															<button
																className="action-btn assign-btn"
																onClick={(e) => handleAssignCase(caseItem, e)}
															>
																<svg width="14" height="14" viewBox="0 0 24 24" fill="none">
																	<path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
																	<circle cx="9" cy="7" r="4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
																	<path d="M22 21v-2a4 4 0 0 0-3-3.87" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
																	<path d="M16 3.13a4 4 0 0 1 0 7.75" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
																</svg>
															</button>
														</Tooltip>
													)}
												</div>
											</div>
										)}

										{/* Steps Summary */}
										<div className="steps-summary">
											<div className="step-item completed">
												<span className="step-label">Completed</span>
												<span className="step-value">{completedSteps}</span>
											</div>
											<div className="step-item in-progress">
												<span className="step-label">In Progress</span>
												<span className="step-value">{caseItem.currentStep - completedSteps}</span>
											</div>
											<div className="step-item pending">
												<span className="step-label">Pending</span>
												<span className="step-value">{pendingSteps}</span>
											</div>
										</div>

										{/* Steps History */}
										<div className="steps-history">
											<h5 className="history-title">Steps History</h5>
											<div className="steps-timeline">
												{caseItem.steps.map((step, index) => (
													<div key={step.stepId} className={`step-timeline-item ${step.status}`}>
														<div className="step-timeline-icon">
															{getStepStatusIcon(step.status)}
														</div>
														<div className="step-timeline-content">
															<div className="step-timeline-header">
																<span className="step-timeline-name">{step.stepName}</span>
																<span className="step-timeline-status">{getStatusText(step.status)}</span>
															</div>
															<div className="step-timeline-details">
																{step.assignedTo && (
																	<span className="step-assignee">Assigned to: {step.assignedTo}</span>
																)}
																{step.startDate && (
																	<span className="step-date">Started: {formatDate(step.startDate)}</span>
																)}
																{step.completionDate && (
																	<span className="step-date">Completed: {formatDate(step.completionDate)}</span>
																)}
																{step.duration && (
																	<span className="step-duration">Duration: {step.duration}</span>
																)}
															</div>
														</div>
													</div>
												))}
											</div>
										</div>

										{/* Dates */}
										<div className="case-dates">
											<div className="date-item">
												<span className="date-label">Started:</span>
												<span className="date-value">{formatDate(caseItem.startDate)}</span>
											</div>
											{caseItem.completionDate && (
												<div className="date-item">
													<span className="date-label">Completed:</span>
													<span className="date-value">{formatDate(caseItem.completionDate)}</span>
												</div>
											)}
											<div className="date-item">
												<span className="date-label">Last Activity:</span>
												<span className="date-value">{formatDate(caseItem.lastActivityDate)}</span>
											</div>
										</div>
									</div>
								)}
							</div>
						);
					})}
				</div>


			</div>
		</Card>
	);
}

appsmith.onReady(() => {
	/*
	 * This handler function will get called when parent application is ready.
	 * Initialize your component here
	 * more info - https://docs.appsmith.com/reference/widgets/custom#onready
	 */
	reactDom.render(<App />, document.getElementById("root"));
});

