uncompiledSrcDoc.css:
html, body {
	margin: 0;
	padding: 0;
	height: auto;
	overflow: visible;
}

.ant-card .ant-card-body {
	padding: 0
}

.app {
	width: 100%;/*calc(1px * var(--appsmith-ui-width));*/
	justify-content: center;
	border-radius: 0px;
	border: none;
	box-shadow: none;
	background: transparent;
	color: #1e3a8a;
	overflow: visible;

	.case-list-container {
		/*padding: 20px;*/
		width: 100%;
		max-width: 1200px;
		margin: 0 auto;

		.kpi-section {
			margin-bottom: 24px;

			.kpi-grid {
				display: grid;
				grid-template-columns: repeat(5, 1fr);
				gap: 16px;

				.kpi-card {
					background: white;
					border: 1px solid #e2e8f0;
					border-radius: 0px;
					padding: 20px;
					display: flex;
					align-items: center;
					gap: 16px;
					transition: all 0.3s ease;

					&:hover {
						transform: translateY(-2px);
						box-shadow: 0 8px 16px rgba(30, 58, 138, 0.1);
					}

					&.total {
						border-left: 4px solid #1e3a8a;
						background: linear-gradient(135deg, rgba(30, 58, 138, 0.1) 0%, rgba(30, 58, 138, 0.05) 100%);
						color: #1e3a8a;

						.kpi-icon {
							background: rgba(30, 58, 138, 0.1);
							color: #1e3a8a;
						}
						.kpi-value {
							color: #1e3a8a;
						}
					}

					&.completed {
						border-left: 4px solid #15803d;
						background: linear-gradient(135deg, rgba(21, 128, 61, 0.1) 0%, rgba(21, 128, 61, 0.05) 100%);
						color: #15803d;

						.kpi-icon {
							background: rgba(21, 128, 61, 0.1);
							color: #15803d;
						}
						.kpi-value {
							color: #15803d;
						}
					}

					&.in-progress {
						border-left: 4px solid #f59e0b;
						background: linear-gradient(135deg, rgba(245, 158, 11, 0.1) 0%, rgba(245, 158, 11, 0.05) 100%);
						color: #92400e;

						.kpi-icon {
							background: rgba(245, 158, 11, 0.1);
							color: #f59e0b;
						}
						.kpi-value {
							color: #f59e0b;
						}
					}

					&.error {
						border-left: 4px solid #f5222d;
						background: linear-gradient(135deg, rgba(245, 34, 45, 0.1) 0%, rgba(245, 34, 45, 0.05) 100%);
						color: #a8071a;

						.kpi-icon {
							background: rgba(245, 34, 45, 0.1);
							color: #f5222d;
						}
						.kpi-value {
							color: #f5222d;
						}
					}

					&.cancelled {
						border-left: 4px solid #6b7280;
						background: linear-gradient(135deg, rgba(107, 114, 128, 0.1) 0%, rgba(107, 114, 128, 0.05) 100%);
						color: #374151;

						.kpi-icon {
							background: rgba(107, 114, 128, 0.1);
							color: #6b7280;
						}
						.kpi-value {
							color: #6b7280;
						}
					}

					.kpi-icon {
						width: 48px;
						height: 48px;
						border-radius: 0px;
						display: flex;
						align-items: center;
						justify-content: center;
						flex-shrink: 0;
					}

					.kpi-content {
						flex: 1;

						.kpi-value {
							font-size: 24px;
							font-weight: 700;
							line-height: 1;
							margin-bottom: 4px;
						}

						.kpi-label {
							font-size: 14px;
							color: #6b7280;
							font-weight: 500;
							margin-bottom: 2px;
						}

						.kpi-percentage {
							font-size: 12px;
							font-weight: 600;
							text-transform: uppercase;
							letter-spacing: 0.5px;
						}
					}
				}
			}
		}

		.case-header {
			display: flex;
			justify-content: space-between;
			align-items: center;
			margin-bottom: 24px;
			padding: 0px;
			background: transparent;
			border-radius: 0px;
			border: none;

			.header-left {
				display: flex;
				align-items: center;
				gap: 12px;

				h3 {
					margin: 0;
					color: #1e3a8a;
					font-size: 18px;
					font-weight: 600;
				}

				.case-count {
					background: #1e3a8a;
					color: white;
					padding: 4px 8px;
					border-radius: 0px;
					font-size: 12px;
					font-weight: 500;
				}
			}

			.header-right {
				display: flex;
				gap: 12px;
				align-items: center;

				.search-input {
					padding: 8px 12px;
					border: 1px solid #d1d5db;
					border-radius: 0px;
					font-size: 14px;
					width: 200px;
					transition: all 0.3s ease;

					&:focus {
						outline: none;
						border-color: #1e3a8a;
						box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
					}
				}

				.status-filter {
					padding: 8px 12px;
					border: 1px solid #d1d5db;
					border-radius: 0px;
					font-size: 14px;
					background: white;
					cursor: pointer;
					transition: all 0.3s ease;

					&:focus {
						outline: none;
						border-color: #1e3a8a;
						box-shadow: 0 0 0 3px rgba(30, 58, 138, 0.1);
					}
				}
			}
		}

		.case-list {
			display: flex;
			flex-direction: column;
			gap: 8px;
			margin-bottom: 24px;

			.case-list-item {
				background: white;
				border: 1px solid #e2e8f0;
				border-radius: 0px;
				transition: all 0.3s ease;
				position: relative;
				overflow: hidden;

				&:hover {
					border-color: #1e3a8a;
					box-shadow: 0 4px 12px rgba(30, 58, 138, 0.1);
				}

				&.selected {
					border-color: #1e3a8a;
					background: #f0f4ff;
					box-shadow: 0 4px 12px rgba(30, 58, 138, 0.15);
				}

				.case-list-header {
					display: flex;
					align-items: center;
					padding: 16px 20px;
					gap: 20px;
					cursor: pointer;

					.case-basic-info {
						flex: 1;
						display: flex;
						flex-direction: column;
						gap: 8px;

						.case-id-status {
							display: flex;
							align-items: center;
							gap: 12px;

							.case-id {
								margin: 0;
								color: #1e3a8a;
								font-size: 16px;
								font-weight: 600;
								line-height: 1.3;
							}

							.status-badge {
								.ant-badge-status-dot {
									width: 8px;
									height: 8px;
								}
								.ant-badge-status-text {
									font-size: 12px;
									font-weight: 500;
								}
							}
						}

						.case-details {
							display: flex;
							flex-direction: column;
							gap: 4px;

							.process-name {
								color: #374151;
								font-size: 14px;
								font-weight: 500;
							}

							.assigned-user {
								color: #6b7280;
								font-size: 13px;
							}

							.dates-line {
								display: flex;
								align-items: center;
								gap: 8px;
								flex-wrap: wrap;
							}

							.case-start-date {
								color: #6b7280;
								font-size: 12px;
								font-weight: 500;
							}

							.date-separator {
								color: #d1d5db;
								font-size: 12px;
								font-weight: 500;
							}

							.summary-date {
								font-size: 12px;
								font-weight: 600;
							}
						}
					}

					.case-progress-info {
						display: flex;
						align-items: center;
						min-width: 120px;

						.progress-mini {
							display: flex;
							flex-direction: column;
							align-items: center;
							gap: 4px;
							width: 100%;

							.progress-text {
								font-size: 12px;
								color: #1e3a8a;
								font-weight: 600;
							}

							.progress-bar-mini {
								width: 100%;
								.ant-progress-bg {
									background: linear-gradient(90deg, #1890ff 0%, #15803d 100%);
								}
							}
						}
					}

					.case-actions {
						display: flex;
						gap: 4px;
						align-items: center;

						.action-btn {
							width: 32px;
							height: 32px;
							border: none;
							border-radius: 0px;
							cursor: pointer;
							display: flex;
							align-items: center;
							justify-content: center;
							transition: all 0.3s ease;
							background: rgba(30, 58, 138, 0.1);
							color: #1e3a8a;

							&:hover {
								background: rgba(30, 58, 138, 0.2);
								transform: scale(1.1);
							}

							&.edit-btn:hover {
								background: rgba(59, 130, 246, 0.2);
								color: #3b82f6;
							}

							&.close-btn:hover {
								background: rgba(239, 68, 68, 0.2);
								color: #ef4444;
							}

							&.assign-btn:hover {
								background: rgba(245, 158, 11, 0.2);
								color: #f59e0b;
							}
						}

						.expand-btn {
							width: 32px;
							height: 32px;
							border: none;
							border-radius: 0px;
							cursor: pointer;
							display: flex;
							align-items: center;
							justify-content: center;
							transition: all 0.3s ease;
							background: rgba(30, 58, 138, 0.1);
							color: #1e3a8a;

							&:hover {
								background: rgba(30, 58, 138, 0.2);
							}

							&.expanded {
								transform: rotate(180deg);
							}

							svg {
								transition: transform 0.3s ease;
							}
						}
					}
				}

				.case-expanded-details {
					padding: 20px;
					background: #f8fafc;
					border-top: 1px solid #e2e8f0;

					.current-step {
						background: #f0f4ff;
						padding: 12px;
						border-radius: 0px;
						margin-bottom: 16px;
						border-left: 4px solid #1e3a8a;

						.current-step-header {
							display: flex;
							justify-content: space-between;
							align-items: flex-start;
							gap: 12px;

							.current-step-info {
								flex: 1;

								.current-step-label {
									display: block;
									font-size: 11px;
									color: #6b7280;
									font-weight: 500;
									text-transform: uppercase;
									letter-spacing: 0.5px;
									margin-bottom: 4px;
								}

								.current-step-name {
									display: block;
									font-size: 14px;
									color: #1e3a8a;
									font-weight: 600;
									margin-bottom: 2px;
								}

								.current-step-assignee {
									font-size: 12px;
									color: #6b7280;
								}
							}

							.action-btn {
								width: 28px;
								height: 28px;
								border: none;
								border-radius: 0px;
								cursor: pointer;
								display: flex;
								align-items: center;
								justify-content: center;
								transition: all 0.3s ease;
								background: rgba(30, 58, 138, 0.1);
								color: #1e3a8a;
								flex-shrink: 0;

								&:hover {
									background: rgba(245, 158, 11, 0.2);
									color: #f59e0b;
									transform: scale(1.1);
								}
							}
						}
					}

					.steps-summary {
						display: grid;
						grid-template-columns: repeat(3, 1fr);
						gap: 12px;
						margin-bottom: 16px;

						.step-item {
							display: flex;
							flex-direction: column;
							align-items: center;
