				grid-template-columns: repeat(2, 1fr);
				gap: 12px;

				.kpi-card {
					padding: 16px;
					gap: 12px;

					.kpi-icon {
						width: 40px;
						height: 40px;
					}

					.kpi-content {
						.kpi-value {
							font-size: 20px;
						}

						.kpi-label {
							font-size: 12px;
						}
					}
				}
			}
		}

		.case-header {
			flex-direction: column;
			gap: 16px;
			align-items: stretch;

			.header-right {
				flex-direction: column;
				gap: 8px;

				.search-input,
				.status-filter {
					width: 100%;
				}
			}
		}

		.case-list {
			.case-list-item {
				.case-list-header {
					flex-direction: column;
					align-items: stretch;
					gap: 12px;

					.case-basic-info {
						.case-id-status {
							justify-content: space-between;
						}
					}

					.case-progress-info {
						min-width: auto;
					}

					.case-actions {
						justify-content: center;
					}
				}

				.case-expanded-details {
					.steps-summary {
						grid-template-columns: 1fr;
					}

					.steps-history {
						.steps-timeline {
							.step-timeline-item {
								.step-timeline-content {
									.step-timeline-header {
										flex-direction: column;
										align-items: flex-start;
										gap: 4px;
									}

									.step-timeline-details {
										flex-direction: column;
										gap: 4px;
									}
								}
							}
						}
					}

					.case-dates {
						grid-template-columns: 1fr;
					}
				}
			}
		}
	}
}

@media (max-width: 480px) {
	.app .case-list-container {
		.kpi-section {
			.kpi-grid {
				grid-template-columns: repeat(2, 1fr);
				gap: 8px;

				.kpi-card {
					padding: 12px;
					gap: 8px;

					.kpi-icon {
						width: 32px;
						height: 32px;
					}

					.kpi-content {
						.kpi-value {
							font-size: 18px;
						}

						.kpi-label {
							font-size: 11px;
						}
					}
				}
			}
		}
	}
}

/* Animations */
@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(20px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

.case-list-item {
	animation: fadeIn 0.3s ease-out;
}

@keyframes expand {
	from {
		max-height: 0;
		opacity: 0;
	}
	to {
		max-height: 500px;
		opacity: 1;
	}
}

.case-expanded-details {
	animation: expand 0.3s ease-out;
}

