import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card, Badge, Tooltip, Progress, Collapse } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'
import Markdown from 'https://cdn.jsdelivr.net/npm/react-markdown@9.0.1/+esm';

function App() {
	const [selectedCase, setSelectedCase] = React.useState(null);
	const [searchTerm, setSearchTerm] = React.useState('');
	const [statusFilter, setStatusFilter] = React.useState('all');
	const [expandedCases, setExpandedCases] = React.useState(new Set());

	const cases = appsmith.model.cases || [];

	// Filtrer les cas
	const filteredCases = cases.filter(caseItem => {
		const matchesSearch = searchTerm === '' || 
			caseItem.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.processName.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.assignedUser.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
			caseItem.assignedUser.lastName.toLowerCase().includes(searchTerm.toLowerCase());

		const matchesStatus = statusFilter === 'all' || statusFilter === '' || caseItem.status === statusFilter;

		return matchesSearch && matchesStatus;
	});

	const handleCaseClick = (caseItem) => {
		setSelectedCase(caseItem);
		appsmith.triggerEvent("onCaseClick", {
			case: caseItem
		});
	};



	const handleEditCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onEditCaseClick", {
			case: caseItem
		});
	};

	const handleCloseCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onCloseCaseClick", {
			case: caseItem
		});
	};

	const handleAssignCase = (caseItem, event) => {
		event.stopPropagation();
		appsmith.triggerEvent("onAssignCaseClick", {
			case: caseItem
		});
	};

	const toggleCaseExpansion = (caseId, event) => {
		event.stopPropagation();
		const newExpanded = new Set(expandedCases);
		if (newExpanded.has(caseId)) {
			newExpanded.delete(caseId);
		} else {
			newExpanded.add(caseId);
		}
		setExpandedCases(newExpanded);
	};

	const getStatusColor = (status) => {
		switch (status) {
			case 'completed': return 'success';
			case 'in_progress': return 'processing';
			case 'pending': return 'warning';
			case 'error': return 'error';
			case 'cancelled': return 'error';
			default: return 'default';
		}
	};

	const getStatusText = (status) => {
		switch (status) {
			case 'completed': return 'Completed';
			case 'in_progress': return 'In Progress';
			case 'pending': return 'Pending';
			case 'error': return 'Error';
			case 'cancelled': return 'Cancelled';
			default: return 'Unknown';
		}
	};

	const formatDate = (dateString) => {
		if (!dateString) return '-';
		return new Date(dateString).toLocaleDateString('en-US', {
			day: '2-digit',
			month: '2-digit',
			year: 'numeric',
			hour: '2-digit',
			minute: '2-digit'
		});
	};

	const getCurrentStepInfo = (caseItem) => {
		const currentStep = caseItem.steps.find(step => step.status === 'in_progress');
		if (currentStep) {
			return {
				name: currentStep.stepName,
				assignedTo: currentStep.assignedTo,
				startDate: currentStep.startDate
			};
		}
		return null;
	};

	const getCompletedStepsCount = (caseItem) => {
		return caseItem.steps.filter(step => step.status === 'completed').length;
	};

	const getPendingStepsCount = (caseItem) => {
		return caseItem.steps.filter(step => step.status === 'pending').length;
	};

	const getStepStatusIcon = (status) => {
		switch (status) {
			case 'completed':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<path d="M20 6L9 17l-5-5" stroke="#15803d" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'in_progress':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#3b82f6" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#3b82f6" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'pending':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#d97706" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#d97706" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
					</svg>
				);
			case 'error':
				return (
					<svg width="16" height="16" viewBox="0 0 24 24" fill="none">
						<circle cx="12" cy="12" r="10" stroke="#f5222d" strokeWidth="2"/>
						<path d="M12 6v6l4 2" stroke="#f5222d" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
