			default:
				return null;
		}
	};

	const getSummaryDate = (caseItem) => {
		if (caseItem.status === 'completed' && caseItem.completionDate) {
			return {
				label: 'Completed',
				date: caseItem.completionDate,
				color: '#15803d'
			};
		} else {
			const currentStepInfo = getCurrentStepInfo(caseItem);
			if (currentStepInfo && currentStepInfo.startDate) {
				return {
					label: 'Current Step Started',
					date: currentStepInfo.startDate,
					color: '#3b82f6'
				};
			}
		}
		return null;
	};

	// Calculate KPIs
	const calculateKPIs = (cases) => {
		const totalCases = cases.length;
		const completedCases = cases.filter(c => c.status === 'completed').length;
		const inProgressCases = cases.filter(c => c.status === 'in_progress').length;
		const errorCases = cases.filter(c => c.status === 'error').length;
		const cancelledCases = cases.filter(c => c.status === 'cancelled').length;
		const completionRate = totalCases > 0 ? Math.round((completedCases / totalCases) * 100) : 0;
		const averageCompletionPercentage = cases.length > 0 
			? Math.round(cases.reduce((sum, c) => sum + (c.completionPercentage || 0), 0) / cases.length)
			: 0;

		return {
			totalCases,
			completedCases,
			inProgressCases,
			errorCases,
			cancelledCases,
			completionRate,
			averageCompletionPercentage
		};
	};

	const kpis = calculateKPIs(cases);

	return (
		<Card className="app">
			<div className="case-list-container">
				{/* KPI Section */}
				<div className="kpi-section">
