							<option value="cancelled">Cancelled</option>
						</select>
					</div>
				</div>

				{/* Case List */}
				<div className="case-list">
					{filteredCases.map((caseItem) => {
						const currentStepInfo = getCurrentStepInfo(caseItem);
						const completedSteps = getCompletedStepsCount(caseItem);
						const pendingSteps = getPendingStepsCount(caseItem);
						const isExpanded = expandedCases.has(caseItem.id);

						return (
							<div
								key={caseItem.id}
								className={`case-list-item ${selectedCase?.id === caseItem.id ? 'selected' : ''}`}
								onClick={() => handleCaseClick(caseItem)}
							>
								{/* Case Header Row */}
								<div className="case-list-header">
