actionConfiguration.body:
function (sectionIndex, fieldIndex, direction) {
  const newSections = [...JSData.sections];
  const section = {
    ...newSections[sectionIndex]
  };
  const data = [...section.data];
  if (direction === "up" && fieldIndex > 0) {
    [data[fieldIndex - 1], data[fieldIndex]] = [data[fieldIndex], data[fieldIndex - 1]];
  }
  if (direction === "down" && fieldIndex < data.length - 1) {
    [data[fieldIndex + 1], data[fieldIndex]] = [data[fieldIndex], data[fieldIndex + 1]];
  }
  section.data = data;
  newSections[sectionIndex] = section;
  JSData.sections = newSections;
  JSSections.sections = newSections;
  JSSections.version = Date.now();
  storeValue("refreshKey", Date.now());
}

