	stepActions: [],
	parameters: {
		name: "",
		version: "",
		description: "",
		token: "",
		displayName: "",
		cancellationDelay: 0,
		documentFolder: ""
	},

	init () {
		if (this.sections.length === 0) {
			this.sections = [...JSMock.sections]
		}
		if (this.steps.length === 0) {
			this.steps = [...JSMock.steps]
		}
		if (this.categories.length === 0) {
			this.categories = [...JSMock.categories]
		}
		JSSections.sections = this.sections;
		storeValue("refreshKey", Date.now());
	},
	createSection () {
		this.sections.push (
			{
				id: Date.now(),
				ref: "",
				title: "",
				description: "",
				data: []
			}
		)
	},
	moveField(sectionIndex, fieldIndex, direction) {
		const newSections = [...this.sections]; // copie du tableau principal
		const section = { ...newSections[sectionIndex] }; // copie de la section ciblée
		const data = [...section.data]; // copie du tableau de champs

		if (direction === "up" && fieldIndex > 0) {
			[data[fieldIndex - 1], data[fieldIndex]] = [data[fieldIndex], data[fieldIndex - 1]];
		}
		if (direction === "down" && fieldIndex < data.length - 1) {
			[data[fieldIndex + 1], data[fieldIndex]] = [data[fieldIndex], data[fieldIndex + 1]];
		}

		section.data = data;
		newSections[sectionIndex] = section;
		this.sections = newSections; // 🔁 remplacement = déclenche mise à jour
		JSSections.sections = newSections
		JSSections.version = Date.now();
		storeValue("refreshKey", Date.now());
	},

	createData (sectionId) {

	}
}

